import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { 
  FaCheckCircle, 
  FaTimesCircle, 
  FaChartBar, 
  FaClipboardList, 
  FaSpinner,
  FaSignOutAlt,
  FaSearch,
  FaFilter
} from 'react-icons/fa';

const DashboardMinistere = () => {
  const navigate = useNavigate();
  const [offers, setOffers] = useState([]);
  const [offresEnAttente, setOffresEnAttente] = useState([]);
  const [stats, setStats] = useState({ total: 0, actifs: 0, rejetees: 0 });
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState({
    dashboard: false,
    pending: false,
    actions: {}
  });
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    date: '',
    statut: 'tous'
  });

  const token = localStorage.getItem('token');

  useEffect(() => {
    fetchDashboard();
    fetchOffresEnAttente();
  }, []);

  const fetchDashboard = async () => {
    setLoading(prev => ({ ...prev, dashboard: true }));
    try {
      const res = await axios.get('http://localhost:3000/api/dashboard/ministere', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOffers(res.data.recentOffers);
      setStats(res.data.stats);
    } catch (err) {
      console.error('Erreur chargement dashboard :', err);
      setMessage('Erreur lors du chargement des données');
    } finally {
      setLoading(prev => ({ ...prev, dashboard: false }));
    }
  };

  const fetchOffresEnAttente = async () => {
    setLoading(prev => ({ ...prev, pending: true }));
    try {
      const res = await axios.get('http://localhost:3000/api/offres/en-attente', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOffresEnAttente(res.data);
    } catch (err) {
      console.error('Erreur chargement offres en attente :', err);
      setMessage('Erreur lors du chargement des offres en attente');
    } finally {
      setLoading(prev => ({ ...prev, pending: false }));
    }
  };

  const validerOffre = async (id) => {
    setLoading(prev => ({ ...prev, actions: { ...prev.actions, [id]: 'validation' } }));
    try {
      await fetch(`http://localhost:3000/api/ministere/valider/${id}`, {
        method: 'POST',
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessage('Offre validée avec succès');
      await Promise.all([fetchDashboard(), fetchOffresEnAttente()]);
    } catch (err) {
      console.error('Erreur validation offre :', err);
      setMessage('Erreur lors de la validation');
    } finally {
      setLoading(prev => ({ ...prev, actions: { ...prev.actions, [id]: false } }));
    }
  };

  const rejeterOffre = async (id) => {
    setLoading(prev => ({ ...prev, actions: { ...prev.actions, [id]: 'rejet' } }));
    try {
      await fetch(`http://localhost:3000/api/ministere/rejeter/${id}`, {
        method: 'POST',
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessage('Offre rejetée avec succès');
      await Promise.all([fetchDashboard(), fetchOffresEnAttente()]);
    } catch (err) {
      console.error('Erreur rejet offre :', err);
      setMessage('Erreur lors du rejet');
    } finally {
      setLoading(prev => ({ ...prev, actions: { ...prev.actions, [id]: false } }));
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  const filteredOffers = offers.filter(offer => {
    const matchesSearch = offer.title.toLowerCase().includes(searchTerm.toLowerCase()) || 
                         offer.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesFilter = filters.statut === 'tous' || 
                         (filters.statut === 'actives' && offer.status === 'active') || 
                         (filters.statut === 'rejetees' && offer.status === 'rejected');
    return matchesSearch && matchesFilter;
  });

  const filteredPendingOffers = offresEnAttente.filter(offer => {
    return offer.title.toLowerCase().includes(searchTerm.toLowerCase()) || 
           offer.description.toLowerCase().includes(searchTerm.toLowerCase());
  });

  return (
    <div className="min-h-screen p-4 md:p-10 bg-gradient-to-r from-blue-900 via-indigo-900 to-purple-900 text-white">
      {/* Header avec bouton de déconnexion */}
      <header className="flex justify-between items-center mb-8">
        <h1 className="text-2xl md:text-4xl font-bold">Espace Ministère</h1>
        <button
          onClick={handleLogout}
          className="flex items-center gap-2 bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg transition-colors"
        >
          <FaSignOutAlt /> Déconnexion
        </button>
      </header>

      {/* Barre de recherche et filtres */}
      <div className="mb-8 bg-white bg-opacity-10 p-4 rounded-xl">
        <div className="flex flex-col md:flex-row gap-4 mb-4">
          <div className="relative flex-grow">
            <FaSearch className="absolute left-3 top-3 text-gray-300" />
            <input
              type="text"
              placeholder="Rechercher une offre..."
              className="w-full pl-10 pr-4 py-2 bg-white bg-opacity-20 rounded-lg text-white placeholder-gray-300"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
          <div className="flex gap-2">
            <select
              className="bg-white bg-opacity-20 text-white px-3 py-2 rounded-lg"
              value={filters.statut}
              onChange={(e) => setFilters({...filters, statut: e.target.value})}
            >
              <option value="tous">Tous les statuts</option>
              <option value="actives">Offres actives</option>
              <option value="rejetees">Offres rejetées</option>
            </select>
            <button className="flex items-center gap-2 bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2 rounded-lg">
              <FaFilter /> Filtrer
            </button>
          </div>
        </div>
      </div>

      {/* Statistiques */}
      {loading.dashboard ? (
        <div className="flex justify-center items-center h-40">
          <FaSpinner className="animate-spin text-3xl text-indigo-300" />
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-10">
          <StatBox icon={<FaChartBar size={30} />} title="Offres totales" value={stats.total} />
          <StatBox icon={<FaCheckCircle size={30} />} title="Offres actives" value={stats.actifs} />
          <StatBox icon={<FaTimesCircle size={30} />} title="Offres rejetées" value={stats.rejetees} />
        </div>
      )}

      {message && (
        <p className={`text-center mb-4 ${
          message.includes('succès') ? 'text-green-300' : 'text-red-300'
        }`}>
          {message}
        </p>
      )}

      {/* Offres en attente */}
      <section className="mb-10 bg-white bg-opacity-10 p-6 rounded-xl">
        <h2 className="text-2xl font-semibold mb-4">Offres en Attente de Validation</h2>
        {loading.pending ? (
          <div className="flex justify-center items-center h-40">
            <FaSpinner className="animate-spin text-3xl text-indigo-300" />
          </div>
        ) : filteredPendingOffers.length === 0 ? (
          <p className="text-gray-300 italic">Aucune offre en attente de validation</p>
        ) : (
          <div className="space-y-4">
            {filteredPendingOffers.map(offer => (
              <OfferCard 
                key={offer.id}
                offer={offer}
                onValidate={() => validerOffre(offer.id)}
                onReject={() => rejeterOffre(offer.id)}
                loading={loading.actions[offer.id]}
                isPending={true}
              />
            ))}
          </div>
        )}
      </section>

      {/* Dernières offres */}
      <section className="bg-white bg-opacity-10 p-6 rounded-xl">
        <h2 className="text-2xl font-semibold mb-4">Dernières Offres à traiter</h2>
        {loading.dashboard ? (
          <div className="flex justify-center items-center h-40">
            <FaSpinner className="animate-spin text-3xl text-indigo-300" />
          </div>
        ) : filteredOffers.length === 0 ? (
          <p className="text-gray-300 italic">Aucune offre récente à afficher</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {filteredOffers.map(offer => (
              <OfferCard 
                key={offer.id}
                offer={offer}
                onValidate={() => validerOffre(offer.id)}
                onReject={() => rejeterOffre(offer.id)}
                loading={loading.actions[offer.id]}
              />
            ))}
          </div>
        )}
      </section>

      <footer className="mt-16 text-center text-sm text-gray-300 italic">
        Statistiques générées automatiquement – Ministère des Finances © {new Date().getFullYear()}
      </footer>
    </div>
  );
};

const StatBox = ({ icon, title, value }) => (
  <div className="bg-white bg-opacity-10 p-6 rounded-xl shadow-md text-center hover:bg-opacity-20 transition-colors">
    <div className="text-indigo-300 mb-2">{icon}</div>
    <h3 className="text-lg font-semibold">{title}</h3>
    <p className="text-2xl font-bold">{value}</p>
  </div>
);

const OfferCard = ({ offer, onValidate, onReject, loading, isPending = false }) => (
  <div className={`bg-white bg-opacity-10 p-4 rounded-xl shadow-md hover:bg-opacity-20 transition-colors ${isPending ? 'border-l-4 border-yellow-400' : ''}`}>
    <h3 className="text-lg font-bold">{offer.title}</h3>
    <p className="text-sm mb-2 text-gray-200">{offer.description}</p>
    <div className="flex flex-wrap justify-between items-center mt-4 gap-2">
      <div className="space-y-1">
        <p className="text-xs text-indigo-200">Date limite : {offer.deadline}</p>
        {offer.status && (
          <span className={`text-xs px-2 py-1 rounded-full ${
            offer.status === 'active' ? 'bg-green-900 text-green-200' :
            offer.status === 'rejected' ? 'bg-red-900 text-red-200' :
            'bg-yellow-900 text-yellow-200'
          }`}>
            {offer.status === 'active' ? 'Active' : 
             offer.status === 'rejected' ? 'Rejetée' : 'En attente'}
          </span>
        )}
      </div>
      <div className="flex gap-2">
        <button
          onClick={onValidate}
          className="flex items-center gap-1 bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded text-sm disabled:opacity-50"
          disabled={loading}
        >
          {loading === 'validation' ? (
            <FaSpinner className="animate-spin" />
          ) : (
            <FaCheckCircle />
          )}
          Valider
        </button>
        <button
          onClick={onReject}
          className="flex items-center gap-1 bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded text-sm disabled:opacity-50"
          disabled={loading}
        >
          {loading === 'rejet' ? (
            <FaSpinner className="animate-spin" />
          ) : (
            <FaTimesCircle />
          )}
          Rejeter
        </button>
      </div>
    </div>
  </div>
);

export default DashboardMinistere;
