import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaPaperPlane, FaSpinner, FaCheck, FaTimes, FaFilePdf, FaFileWord, FaFileImage } from 'react-icons/fa';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Postuler = () => {
  const [offers, setOffers] = useState([]);
  const [offerId, setOfferId] = useState('');
  const [file, setFile] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [errors, setErrors] = useState({});
  const [filePreview, setFilePreview] = useState(null);

  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchOffers = async () => {
      try {
        const response = await axios.get('/api/entreprise/offres', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setOffers(response.data);
      } catch (err) {
        toast.error('Erreur lors du chargement des offres');
        console.error(err);
      }
    };

    fetchOffers();
  }, [token]);

  const validateForm = () => {
    const newErrors = {};
    if (!offerId) newErrors.offerId = 'Veuillez sélectionner une offre';
    if (!file) newErrors.file = 'Veuillez sélectionner un fichier';
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
      setErrors({...errors, file: null});
      
      // Créer une prévisualisation
      const reader = new FileReader();
      reader.onloadend = () => {
        setFilePreview({
          name: selectedFile.name,
          type: selectedFile.type,
          size: selectedFile.size,
          url: reader.result
        });
      };
      reader.readAsDataURL(selectedFile);
    }
  };

  const getFileIcon = (fileType) => {
    if (fileType.includes('pdf')) return <FaFilePdf className="text-red-500 text-2xl" />;
    if (fileType.includes('word') || fileType.includes('msword')) return <FaFileWord className="text-blue-500 text-2xl" />;
    if (fileType.includes('image')) return <FaFileImage className="text-green-500 text-2xl" />;
    return <FaFilePdf className="text-gray-500 text-2xl" />;
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;
    
    setIsLoading(true);
    setIsSubmitted(false);

    const formData = new FormData();
    formData.append('offerId', offerId);
    formData.append('file', file);

    try {
      await axios.post('/api/entreprise/candidatures', formData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });
      
      toast.success('Candidature soumise avec succès !');
      setIsSubmitted(true);
      setOfferId('');
      setFile(null);
      setFilePreview(null);
    } catch (error) {
      console.error(error);
      toast.error(error.response?.data?.message || 'Erreur lors de la soumission de la candidature');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8 px-4">
      <div className="max-w-2xl mx-auto bg-white rounded-xl shadow-md overflow-hidden">
        <div className="p-8">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-2xl font-bold text-gray-800">Postuler à une offre</h2>
            {isSubmitted && (
              <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-green-100 text-green-800">
                <FaCheck className="mr-1" /> Soumis
              </span>
            )}
          </div>

          <form onSubmit={handleSubmit} className="space-y-6">
            <div>
              <label htmlFor="offer" className="block text-sm font-medium text-gray-700 mb-1">
                Offre d'emploi <span className="text-red-500">*</span>
              </label>
              <select
                id="offer"
                value={offerId}
                onChange={(e) => {
                  setOfferId(e.target.value);
                  setErrors({...errors, offerId: null});
                }}
                className={`block w-full px-4 py-2 border rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 ${
                  errors.offerId ? 'border-red-500' : 'border-gray-300'
                }`}
              >
                <option value="">-- Sélectionner une offre --</option>
                {offers.map((offer) => (
                  <option key={offer.id} value={offer.id}>
                    {offer.title} - {offer.company}
                  </option>
                ))}
              </select>
              {errors.offerId && (
                <p className="mt-1 text-sm text-red-600">{errors.offerId}</p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                CV / Lettre de motivation <span className="text-red-500">*</span>
              </label>
              
              {filePreview ? (
                <div className="border border-gray-300 rounded-md p-4 flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    {getFileIcon(filePreview.type)}
                    <div>
                      <p className="text-sm font-medium text-gray-900 truncate max-w-xs">{filePreview.name}</p>
                      <p className="text-xs text-gray-500">{formatFileSize(filePreview.size)}</p>
                    </div>
                  </div>
                  <button
                    type="button"
                    onClick={() => {
                      setFile(null);
                      setFilePreview(null);
                    }}
                    className="text-red-500 hover:text-red-700"
                  >
                    <FaTimes />
                  </button>
                </div>
              ) : (
                <>
                  <div className="flex items-center justify-center w-full">
                    <label
                      htmlFor="file-upload"
                      className={`flex flex-col items-center justify-center w-full h-32 border-2 border-dashed rounded-lg cursor-pointer ${
                        errors.file ? 'border-red-500 bg-red-50' : 'border-gray-300 hover:border-blue-500'
                      }`}
                    >
                      <div className="flex flex-col items-center justify-center pt-5 pb-6">
                        <FaPaperPlane className="w-8 h-8 mb-3 text-gray-400" />
                        <p className="mb-2 text-sm text-gray-500">
                          <span className="font-semibold">Cliquez pour uploader</span> ou glissez-déposez
                        </p>
                        <p className="text-xs text-gray-500">PDF, DOCX, JPG (Max. 5MB)</p>
                      </div>
                      <input
                        id="file-upload"
                        type="file"
                        onChange={handleFileChange}
                        className="hidden"
                        accept=".pdf,.doc,.docx,.jpg,.jpeg,.png"
                      />
                    </label>
                  </div>
                  {errors.file && (
                    <p className="mt-1 text-sm text-red-600">{errors.file}</p>
                  )}
                </>
              )}
            </div>

            <div className="pt-2">
              <button
                type="submit"
                disabled={isLoading}
                className={`w-full flex justify-center items-center py-3 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 ${
                  isLoading ? 'opacity-75 cursor-not-allowed' : ''
                }`}
              >
                {isLoading ? (
                  <>
                    <FaSpinner className="animate-spin mr-2" />
                    Envoi en cours...
                  </>
                ) : (
                  <>
                    <FaPaperPlane className="mr-2" />
                    Soumettre ma candidature
                  </>
                )}
              </button>
            </div>
          </form>

          <div className="mt-6 border-t border-gray-200 pt-4">
            <h3 className="text-sm font-medium text-gray-900">Conseils pour votre candidature</h3>
            <ul className="mt-2 text-sm text-gray-600 space-y-1">
              <li>• Vérifiez l'orthographe et la grammaire</li>
              <li>• Personnalisez votre lettre de motivation pour chaque offre</li>
              <li>• Gardez votre CV à jour et concis (1-2 pages maximum)</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Postuler;
