import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaChartLine, FaFileAlt, FaUsers, FaSignOutAlt } from 'react-icons/fa';

const EntrepriseHome = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-100 via-blue-200 to-blue-300 flex flex-col items-center p-6 relative">
      {/* Bouton Déconnexion en haut à droite */}
      <button
        onClick={handleLogout}
        className="absolute top-6 right-6 flex items-center gap-2 bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg transition-colors"
      >
        <FaSignOutAlt /> Déconnexion
      </button>

      <h1 className="text-4xl font-bold mb-8 text-blue-900">Espace Entreprise</h1>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-8 w-full max-w-3xl">
        
        {/* Lien vers le Dashboard Entreprise */}
        <Link 
          to="/dashboard/entreprise" 
          className="bg-white rounded-xl shadow-lg p-8 flex flex-col items-center hover:bg-blue-50 transition transform hover:scale-105"
        >
          <FaChartLine className="text-blue-600 text-3xl mb-2" />
          <span className="text-2xl font-semibold mb-2 text-blue-700">Dashboard</span>
          <span className="text-gray-500 text-center">Tableau de bord complet avec statistiques</span>
        </Link>
        
        {/* Lien vers les Offres disponibles */}
        <Link 
          to="/dashboard/entreprise/offres" 
          className="bg-white rounded-xl shadow-lg p-8 flex flex-col items-center hover:bg-blue-50 transition transform hover:scale-105"
        >
          <FaFileAlt className="text-green-600 text-3xl mb-2" />
          <span className="text-2xl font-semibold mb-2 text-blue-700">Offres</span>
          <span className="text-gray-500 text-center">Consulter les offres disponibles</span>
        </Link>
        
        {/* Lien vers le suivi des candidatures */}
        <Link 
          to="/dashboard/entreprise/mes-candidatures" 
          className="bg-white rounded-xl shadow-lg p-8 flex flex-col items-center hover:bg-blue-50 transition transform hover:scale-105"
        >
          <FaUsers className="text-purple-600 text-3xl mb-2" />
          <span className="text-2xl font-semibold mb-2 text-blue-700">Candidatures</span>
          <span className="text-gray-500 text-center">Suivi de vos candidatures</span>
        </Link>

      </div>
    </div>
  );
};

export default EntrepriseHome;
