import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import {
  FaChartLine,
  FaUsers,
  FaSignOutAlt,
  FaPlusCircle,
  FaMoon,
  FaSun
} from 'react-icons/fa';

const MinistereHome = () => {
  const navigate = useNavigate();
  const [isDarkMode, setIsDarkMode] = useState(false);

  // Appliquer la classe dark
  useEffect(() => {
    if (isDarkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [isDarkMode]);

  // Bloquer le rechargement ou la fermeture de l'onglet sans logout
  useEffect(() => {
    const handleBeforeUnload = (e) => {
      e.preventDefault();
      e.returnValue = ''; // Nécessaire pour certains navigateurs (Chrome)
    };

    window.addEventListener('beforeunload', handleBeforeUnload);
    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload);
    };
  }, []);

  // Logout autorisé
  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-indigo-100 via-purple-200 to-blue-300 dark:from-gray-900 dark:via-gray-800 dark:to-black flex flex-col items-center p-6 relative">
      
      {/* Boutons en haut à droite */}
      <div className="absolute top-6 right-6 flex items-center gap-4">
        {/* Mode sombre */}
        <button
          onClick={() => setIsDarkMode(!isDarkMode)}
          className="bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-white p-2 rounded-full transition"
        >
          {isDarkMode ? <FaSun /> : <FaMoon />}
        </button>

        {/* Déconnexion */}
        <button
          onClick={handleLogout}
          className="flex items-center gap-2 bg-indigo-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg transition-colors"
        >
          <FaSignOutAlt /> Déconnexion
        </button>
      </div>

      <h1 className="text-4xl font-bold mb-8 text-indigo-900 dark:text-white">Espace Ministère</h1>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-8 w-full max-w-3xl">
        {/* Dashboard */}
        <Link
          to="/Dashboard/DashbordMinistere"
          className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-8 flex flex-col items-center hover:bg-indigo-50 dark:hover:bg-gray-700 transition transform hover:scale-105"
        >
          <FaChartLine className="text-indigo-600 dark:text-indigo-300 text-3xl mb-2" />
          <span className="text-2xl font-semibold mb-2 text-indigo-700 dark:text-white">Dashboard</span>
          <span className="text-gray-500 dark:text-gray-300 text-center">Tableau de bord complet avec statistiques</span>
        </Link>

        {/* Créer Offre */}
        <Link
          to="/Ministere/CreerOffre"
          className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-8 flex flex-col items-center hover:bg-indigo-50 dark:hover:bg-gray-700 transition transform hover:scale-105"
        >
          <FaPlusCircle className="text-green-600 text-3xl mb-2" />
          <span className="text-2xl font-semibold mb-2 text-indigo-700 dark:text-white">Créer Offre</span>
          <span className="text-gray-500 dark:text-gray-300 text-center">Publier de nouvelles offres d'emploi</span>
        </Link>

        {/* Candidatures */}
        <Link
          to="/Ministere/GestionCandidatures"
          className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-8 flex flex-col items-center hover:bg-indigo-50 dark:hover:bg-gray-700 transition transform hover:scale-105"
        >
          <FaUsers className="text-purple-600 dark:text-purple-300 text-3xl mb-2" />
          <span className="text-2xl font-semibold mb-2 text-indigo-700 dark:text-white">Candidatures</span>
          <span className="text-gray-500 dark:text-gray-300 text-center">Gérer les candidatures reçues</span>
        </Link>
      </div>
    </div>
  );
};

export default MinistereHome;
