import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaUser, FaEnvelope, FaFileAlt } from 'react-icons/fa';

const GestionCandidatures = () => {
  const [candidatures, setCandidatures] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Récupérer les candidatures au chargement
  useEffect(() => {
    const fetchCandidatures = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5000/api/candidatures', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setCandidatures(res.data);
      } catch (err) {
        setError('Erreur lors du chargement des candidatures');
      } finally {
        setLoading(false);
      }
    };

    fetchCandidatures();
  }, []);

  if (loading) return <p>Chargement des candidatures...</p>;
  if (error) return <p className="text-red-600">{error}</p>;

  return (
    <div className="p-6 min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-white">
      <h1 className="text-3xl font-bold mb-6">Gestion des Candidatures</h1>

      {candidatures.length === 0 ? (
        <p>Aucune candidature reçue pour le moment.</p>
      ) : (
        <table className="min-w-full bg-white dark:bg-gray-800 rounded-lg shadow">
          <thead>
            <tr className="text-left border-b border-gray-300 dark:border-gray-700">
              <th className="p-4">Nom de l'entreprise</th>
              <th className="p-4">Offre</th>
              <th className="p-4">Candidat</th>
              <th className="p-4">Email</th>
              <th className="p-4">Date de candidature</th>
              <th className="p-4">Documents</th>
            </tr>
          </thead>
          <tbody>
            {candidatures.map((c) => (
              <tr key={c.id} className="border-b border-gray-200 dark:border-gray-700">
                <td className="p-4">{c.entrepriseName || c.companyName || 'Non renseigné'}</td>
                <td className="p-4">{c.offreTitre || c.jobTitle || 'Non renseigné'}</td>
                <td className="p-4">{c.candidatNom || c.candidateName || 'Non renseigné'}</td>
                <td className="p-4">{c.email}</td>
                <td className="p-4">{new Date(c.dateCandidature || c.createdAt).toLocaleDateString()}</td>
                <td className="p-4">
                  {c.documents && c.documents.length > 0 ? (
                    c.documents.map((doc, i) => (
                      <a
                        key={i}
                        href={doc.url}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-blue-600 hover:underline mr-2"
                      >
                        <FaFileAlt /> Doc {i + 1}
                      </a>
                    ))
                  ) : (
                    'Aucun document'
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default GestionCandidatures;
