import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { FaCalendarAlt, FaFileAlt, FaTag, FaCheckCircle } from "react-icons/fa";
import { motion } from "framer-motion";

const contrats = ["CDI", "CDD", "Stage", "Freelance", "Consultation"];

const fieldVariant = {
  hidden: { opacity: 0, y: 10 },
  visible: (i) => ({
    opacity: 1,
    y: 0,
    transition: { delay: i * 0.1 },
  }),
};

const CreerOffre = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    titre: "",
    description: "",
    domaine: "",
    dateLimite: "",
    typeContrat: contrats[0],
    budget: "",
    lieu: "",
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [success, setSuccess] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((old) => ({ ...old, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    try {
      const token = localStorage.getItem("token");
      await axios.post("http://localhost:3000/api/offres", formData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setSuccess(true);

      // Reset form (optionnel)
      setFormData({
        titre: "",
        description: "",
        domaine: "",
        dateLimite: "",
        typeContrat: contrats[0],
        budget: "",
        lieu: "",
      });

      // Redirige après un délai
      setTimeout(() => {
        navigate("/entreprise"); // <-- page entreprises où on affiche les offres
      }, 1500);
    } catch (error) {
      console.error("Erreur lors de la création de l'offre", error);
      alert("Erreur lors de la création de l'offre.");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 dark:bg-gray-900 p-6 md:p-12 text-gray-900 dark:text-white transition-colors">
      <motion.div
        initial={{ opacity: 0, y: -30 }}
        animate={{ opacity: 1, y: 0 }}
        className="max-w-4xl mx-auto"
      >
        <h2 className="text-3xl font-bold mb-8 text-center">
          Publier un Nouveau Marché Public
        </h2>

        {success ? (
          <motion.div
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            exit={{ scale: 0.8, opacity: 0 }}
            className="bg-green-100 dark:bg-green-900 text-green-800 dark:text-green-200 p-8 rounded-xl text-center shadow-lg"
          >
            <FaCheckCircle className="text-6xl mx-auto mb-4" />
            <h3 className="text-2xl font-semibold mb-2">
              Offre publiée avec succès !
            </h3>
            <p>Redirection vers la page entreprises...</p>
          </motion.div>
        ) : (
          <motion.form
            onSubmit={handleSubmit}
            initial="hidden"
            animate="visible"
            className="bg-white dark:bg-gray-800 shadow-lg rounded-xl p-8"
          >
            {[
              {
                label: "Titre du marché",
                icon: <FaFileAlt />,
                name: "titre",
                type: "text",
                placeholder: "Ex: Fourniture de matériel informatique",
                required: true,
              },
              {
                label: "Domaine",
                icon: <FaTag />,
                name: "domaine",
                type: "text",
                placeholder: "Ex: Informatique, BTP, Santé...",
                required: true,
              },
              {
                label: "Date limite",
                icon: <FaCalendarAlt />,
                name: "dateLimite",
                type: "date",
                min: new Date().toISOString().split("T")[0],
                required: true,
              },
              {
                label: "Type de contrat",
                name: "typeContrat",
                type: "select",
                options: contrats,
              },
              {
                label: "Budget (optionnel)",
                name: "budget",
                type: "number",
                placeholder: "Montant en €",
                required: false,
              },
              {
                label: "Lieu (optionnel)",
                name: "lieu",
                type: "text",
                placeholder: "Ex: Paris, Dakar...",
                required: false,
              },
            ].map((field, i) => (
              <motion.div
                key={field.name}
                custom={i}
                variants={fieldVariant}
                className="mb-6"
              >
                <label
                  className="block font-semibold mb-2 flex items-center gap-2"
                  htmlFor={field.name}
                >
                  {field.icon}
                  {field.label}
                </label>

                {field.type === "select" ? (
                  <select
                    id={field.name}
                    name={field.name}
                    value={formData[field.name]}
                    onChange={handleChange}
                    className="w-full px-4 py-2 rounded-lg border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                  >
                    {field.options.map((opt) => (
                      <option key={opt} value={opt}>
                        {opt}
                      </option>
                    ))}
                  </select>
                ) : (
                  <input
                    id={field.name}
                    name={field.name}
                    type={field.type}
                    value={formData[field.name]}
                    onChange={handleChange}
                    placeholder={field.placeholder || ""}
                    min={field.min}
                    required={field.required}
                    className="w-full px-4 py-2 rounded-lg border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                  />
                )}
              </motion.div>
            ))}

            <motion.div
              className="mb-6"
              custom={7}
              variants={fieldVariant}
            >
              <label
                htmlFor="description"
                className="block font-semibold mb-2"
              >
                Description détaillée
              </label>
              <textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={handleChange}
                rows="6"
                placeholder="Décrivez précisément le marché, les spécifications techniques, les critères d'attribution..."
                required
                className="w-full px-4 py-2 rounded-lg border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
              />
            </motion.div>

            <div className="flex justify-end gap-4">
              <motion.button
                type="button"
                onClick={() => navigate("/ministere")}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                className="px-6 py-2 border border-gray-300 dark:border-gray-600 rounded-lg font-medium"
              >
                Annuler
              </motion.button>

              <motion.button
                type="submit"
                disabled={isSubmitting}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                className="px-6 py-2 bg-indigo-600 hover:bg-indigo-700 text-white rounded-lg font-medium disabled:opacity-50"
              >
                {isSubmitting ? "Publication en cours..." : "Publier le marché"}
              </motion.button>
            </div>
          </motion.form>
        )}
      </motion.div>
    </div>
  );
};

export default CreerOffre;
