import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Postuler = () => {
  const { offerId } = useParams();
  const [offer, setOffer] = useState(null);
  const [file, setFile] = useState(null);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true);

  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchOffer = async () => {
      if (!offerId) {
        setMessage("Identifiant d'offre manquant.");
        setLoading(false);
        return;
      }

      try {
        const res = await axios.get(`/api/offres/${offerId}/details`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setOffer(res.data.offer); // ✅ corriger ici : le backend retourne { offer, candidatures }
      } catch (err) {
        setMessage("Impossible de charger l'offre.");
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchOffer();
  }, [offerId, token]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      setMessage("Veuillez sélectionner un fichier.");
      return;
    }

    const formData = new FormData();
    formData.append('file', file);
    formData.append('offerId', offerId);

    try {
      const res = await axios.post('/api/entreprise/postuler', formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });
      setMessage(res.data.message);
    } catch (err) {
      setMessage("Erreur lors de la soumission de la candidature.");
      console.error(err);
    }
  };

  if (loading) return <div className="p-4 text-center">Chargement de l’offre...</div>;

  if (!offer) return <div className="p-4 text-red-500 text-center">{message}</div>;

  return (
    <div className="p-6 max-w-xl mx-auto bg-white rounded-xl shadow-md space-y-4 mt-6">
      <h2 className="text-2xl font-bold text-indigo-700">{offer.titre}</h2>
      <p><strong>Description :</strong> {offer.description}</p>
      <p><strong>Domaine :</strong> {offer.domaine}</p>
      <p><strong>Date de publication :</strong> {new Date(offer.datePublication).toLocaleDateString()}</p>

      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block font-medium">Joindre votre fichier (PDF, DOCX, etc.)</label>
          <input
            type="file"
            accept=".pdf,.doc,.docx"
            onChange={(e) => setFile(e.target.files[0])}
            className="mt-2 p-2 border rounded w-full"
          />
        </div>
        <button
          type="submit"
          className="bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700"
        >
          Postuler
        </button>
      </form>

      {message && (
        <div className="mt-4 text-center text-sm text-green-600">
          {message}
        </div>
      )}
    </div>
  );
};

export default Postuler;
