// src/Pages/DashboardAdmin.jsx
import React, { useEffect, useState } from 'react';

const DashboardAdmin = () => {
  const [stats, setStats] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    fetch('http://localhost:3000/api/dashboard/admin', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => setStats(data));
  }, []);

  if (!stats) return <p className="p-4">Chargement...</p>;

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold text-blue-700 mb-4">Dashboard Admin</h1>
      <p className="mb-2">üë§ Utilisateurs : {stats.stats.users}</p>
      <p className="mb-2">üè¢ Entreprises : {stats.stats.entreprises}</p>
      <p className="mb-2">üèõÔ∏è Minist√®res : {stats.stats.ministeres}</p>
      <p className="mb-2">üìÑ Offres : {stats.stats.offers}</p>
    </div>
  );
};

export default DashboardAdmin;
