import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { 
  FaClipboardList, 
  FaCheckCircle, 
  FaChartBar,
  FaChartPie,
  FaArrowRight,
  FaSpinner
} from 'react-icons/fa';
import { Bar, Pie } from 'react-chartjs-2';
import { Chart as ChartJS, registerables } from 'chart.js';

ChartJS.register(...registerables);

const DashboardEntreprise = () => {
  const [stats, setStats] = useState({
    totalOffres: 0,
    candidaturesApprouvees: 0,
    candidaturesEnAttente: 0,
    candidaturesRejetees: 0
  });
  const [loading, setLoading] = useState(true);
  const [chartData, setChartData] = useState(null);
  const [latestOffers, setLatestOffers] = useState([]);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:3000/api/dashboard/entreprise', {
          headers: { Authorization: `Bearer ${token}` }
        });

        const { offers, stats } = response.data;
        
        setStats({
          totalOffres: offers.length,
          candidaturesApprouvees: stats.approuvees,
          candidaturesEnAttente: stats.enAttente,
          candidaturesRejetees: stats.rejetees
        });

        setLatestOffers(offers);

        prepareChartData({
          approuvees: stats.approuvees,
          enAttente: stats.enAttente,
          rejetees: stats.rejetees,
          totalOffres: offers.length
        });
      } catch (err) {
        console.error('Erreur chargement dashboard:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  const prepareChartData = (data) => {
    setChartData({
      bar: {
        labels: ['Approuvées', 'En attente', 'Rejetées'],
        datasets: [{
          label: 'Statut des Candidatures',
          data: [data.approuvees, data.enAttente, data.rejetees],
          backgroundColor: [
            'rgba(16, 185, 129, 0.7)', // vert émeraude clair
            'rgba(234, 179, 8, 0.7)', // jaune ambré
            'rgba(220, 38, 38, 0.7)' // rouge
          ],
          borderColor: [
            'rgba(16, 185, 129, 1)',
            'rgba(234, 179, 8, 1)',
            'rgba(220, 38, 38, 1)'
          ],
          borderWidth: 1
        }]
      },
      pie: {
        labels: ['Offres disponibles', 'Candidatures envoyées'],
        datasets: [{
          data: [data.totalOffres, data.approuvees + data.enAttente + data.rejetees],
          backgroundColor: [
            'rgba(59, 130, 246, 0.7)', // bleu clair
            'rgba(168, 85, 247, 0.7)' // violet
          ],
          borderColor: [
            'rgba(59, 130, 246, 1)',
            'rgba(168, 85, 247, 1)'
          ],
          borderWidth: 1
        }]
      }
    });
  };

  return (
    <div className="min-h-screen p-6 bg-gradient-to-r from-blue-100 via-blue-200 to-blue-300 text-blue-900">
      <h1 className="text-3xl font-bold mb-8 text-center">Tableau de Bord Entreprise</h1>

      {loading ? (
        <div className="flex justify-center items-center h-64">
          <FaSpinner className="animate-spin text-4xl text-blue-600" />
        </div>
      ) : (
        <>
          {/* Cartes de Statistiques */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
            <StatCard 
              icon={<FaClipboardList className="text-blue-600" size={24} />}
              title="Offres Disponibles"
              value={stats.totalOffres}
              link="/entreprise/offres"
              linkText="Voir les offres"
            />
            <StatCard 
              icon={<FaCheckCircle className="text-green-600" size={24} />}
              title="Candidatures Approuvées"
              value={stats.candidaturesApprouvees}
              link="/entreprise/candidatures?status=acceptée"
              linkText="Voir détails"
            />
            <StatCard 
              icon={<FaChartBar className="text-yellow-500" size={24} />}
              title="Statut Candidatures"
              value={`${stats.candidaturesEnAttente} en attente`}
              link="/entreprise/candidatures"
              linkText="Voir le suivi"
            />
          </div>

          {/* Dernières offres disponibles */}
          <div className="bg-white bg-opacity-80 p-6 rounded-xl shadow mb-8">
            <h3 className="text-xl font-semibold mb-4">Dernières Offres Disponibles</h3>
            {latestOffers.length > 0 ? (
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {latestOffers.map(offer => (
                  <OfferCard key={offer.id} offer={offer} />
                ))}
              </div>
            ) : (
              <p className="text-blue-700 italic">Aucune offre disponible actuellement</p>
            )}
          </div>

          {/* Visualisations Graphiques */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
            <ChartContainer 
              icon={<FaChartBar className="text-blue-600" />}
              title="Répartition des Candidatures"
            >
              {chartData && <Bar data={chartData.bar} options={chartOptions.bar} />}
            </ChartContainer>
            <ChartContainer 
              icon={<FaChartPie className="text-purple-600" />}
              title="Ratio Offres/Candidatures"
            >
              {chartData && <Pie data={chartData.pie} options={chartOptions.pie} />}
            </ChartContainer>
          </div>
        </>
      )}
    </div>
  );
};

// Composants supplémentaires adaptés au thème
const OfferCard = ({ offer }) => (
  <div className="bg-white bg-opacity-80 p-4 rounded-xl shadow-md hover:bg-opacity-90 transition">
    <h4 className="font-bold text-blue-900">{offer.title}</h4>
    <p className="text-blue-800 text-sm mt-1 truncate">{offer.description}</p>
    <div className="flex justify-between items-center mt-3">
      <span className="text-xs text-blue-600">
        Date limite : {new Date(offer.deadline).toLocaleDateString()}
      </span>
      <Link 
        to={`/entreprise/postuler/${offer._id}`}
        className="text-xs bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded"
      >
        Postuler
      </Link>
    </div>
  </div>
);

const ChartContainer = ({ icon, title, children }) => (
  <div className="bg-white bg-opacity-80 p-6 rounded-xl shadow-md">
    <div className="flex items-center gap-2 mb-4 text-blue-900">
      {icon}
      <h3 className="text-xl font-semibold">{title}</h3>
    </div>
    {children}
  </div>
);

const StatCard = ({ icon, title, value, link, linkText }) => (
  <div className="bg-white bg-opacity-80 p-6 rounded-xl shadow-md hover:bg-opacity-90 transition">
    <div className="flex items-center gap-3 mb-4 text-blue-900">
      {icon}
      <h3 className="text-lg font-semibold">{title}</h3>
    </div>
    <p className="text-3xl font-bold mb-4">{value}</p>
    <Link to={link} className="inline-flex items-center text-blue-700 hover:text-blue-900 text-sm">
      {linkText} <FaArrowRight className="ml-1" />
    </Link>
  </div>
);

// Options des graphiques avec texte blanc/bleu sombre
const chartOptions = {
  bar: {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
        labels: { color: '#1e3a8a' } // bleu foncé
      }
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: { color: '#1e3a8a' },
        grid: { color: 'rgba(30, 58, 138, 0.1)' }
      },
      x: {
        ticks: { color: '#1e3a8a' },
        grid: { color: 'rgba(30, 58, 138, 0.1)' }
      }
    }
  },
  pie: {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
        labels: { color: '#1e3a8a' }
      }
    }
  }
};

export default DashboardEntreprise;
