// ========== FRONTEND (React Component) ==========
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const CreerOffre = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    domaine: '',      // ✅ AJOUTÉ - Champ obligatoire selon le modèle
    dateLimite: '',
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Liste des domaines disponibles
  const domaines = [
    { value: 'informatique', label: 'Informatique' },
    { value: 'construction', label: 'Construction' },
    { value: 'services', label: 'Services' },
    { value: 'transport', label: 'Transport' },
    { value: 'sante', label: 'Santé' },
    { value: 'education', label: 'Éducation' },
    { value: 'agriculture', label: 'Agriculture' },
    { value: 'energie', label: 'Énergie' },
    { value: 'autres', label: 'Autres' }
  ];

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    // Validation côté client
    if (!formData.title.trim()) {
      setError('Le titre est obligatoire');
      setLoading(false);
      return;
    }

    if (!formData.description.trim()) {
      setError('La description est obligatoire');
      setLoading(false);
      return;
    }

    if (!formData.domaine) {
      setError('Le domaine est obligatoire');
      setLoading(false);
      return;
    }

    if (!formData.dateLimite) {
      setError('La date limite est obligatoire');
      setLoading(false);
      return;
    }

    // Validation de la date
    const dateObj = new Date(formData.dateLimite);
    if (dateObj <= new Date()) {
      setError('La date limite doit être dans le futur');
      setLoading(false);
      return;
    }

    try {
      console.log('Données envoyées:', formData);

      const response = await axios.post('/api/offres', {
        title: formData.title,        // ✅ CORRIGÉ - Utilise "title" pas "titre"
        description: formData.description,
        domaine: formData.domaine,    // ✅ AJOUTÉ - Champ obligatoire
        dateLimite: formData.dateLimite
      });

      console.log('Offre créée:', response.data);
      
      // Afficher un message de succès
      alert('Offre créée avec succès !');
      
      // Redirection après création
      navigate('/offres');
    } catch (error) {
      console.error('Erreur complète:', error);
      console.error('Réponse du serveur:', error.response?.data);
      
      if (error.response?.data?.details) {
        // Erreur de validation détaillée
        const details = error.response.data.details;
        if (Array.isArray(details)) {
          setError(`Erreurs de validation: ${details.map(d => d.message).join(', ')}`);
        } else {
          setError(error.response.data.message);
        }
      } else {
        setError(error.response?.data?.message || 'Erreur lors de la création de l\'offre');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: '600px', margin: '20px auto', padding: '20px' }}>
      <h2>Créer une nouvelle offre</h2>
      
      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '15px' }}>
        
        {/* Titre */}
        <div>
          <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
            Titre de l'offre *
          </label>
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            placeholder="Ex: Développement d'une application web"
            style={{
              width: '100%',
              padding: '8px',
              border: '1px solid #ddd',
              borderRadius: '4px',
              fontSize: '16px'
            }}
            required
          />
        </div>

        {/* Description */}
        <div>
          <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
            Description *
          </label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            placeholder="Décrivez en détail l'offre de marché public..."
            rows="5"
            style={{
              width: '100%',
              padding: '8px',
              border: '1px solid #ddd',
              borderRadius: '4px',
              fontSize: '16px',
              resize: 'vertical'
            }}
            required
          />
        </div>

        {/* Domaine */}
        <div>
          <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
            Domaine *
          </label>
          <select
            name="domaine"
            value={formData.domaine}
            onChange={handleChange}
            style={{
              width: '100%',
              padding: '8px',
              border: '1px solid #ddd',
              borderRadius: '4px',
              fontSize: '16px'
            }}
            required
          >
            <option value="">Sélectionnez un domaine</option>
            {domaines.map(domaine => (
              <option key={domaine.value} value={domaine.value}>
                {domaine.label}
              </option>
            ))}
          </select>
        </div>

        {/* Date limite */}
        <div>
          <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
            Date limite de soumission *
          </label>
          <input
            type="date"
            name="dateLimite"
            value={formData.dateLimite}
            onChange={handleChange}
            min={new Date().toISOString().split('T')[0]} // Empêche les dates passées
            style={{
              width: '100%',
              padding: '8px',
              border: '1px solid #ddd',
              borderRadius: '4px',
              fontSize: '16px'
            }}
            required
          />
        </div>

        {/* Bouton de soumission */}
        <button 
          type="submit" 
          disabled={loading}
          style={{
            padding: '12px 20px',
            backgroundColor: loading ? '#ccc' : '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            fontSize: '16px',
            cursor: loading ? 'not-allowed' : 'pointer',
            marginTop: '10px'
          }}
        >
          {loading ? 'Création en cours...' : 'Créer l\'offre'}
        </button>
      </form>

      {/* Affichage des erreurs */}
      {error && (
        <div style={{
          color: 'red',
          backgroundColor: '#fff5f5',
          border: '1px solid #fed7d7',
          borderRadius: '4px',
          padding: '10px',
          marginTop: '15px'
        }}>
          <strong>Erreur:</strong> {error}
        </div>
      )}

      {/* Bouton retour */}
      <button
        type="button"
        onClick={() => navigate('/offres')}
        style={{
          marginTop: '20px',
          padding: '10px 15px',
          backgroundColor: '#6c757d',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer'
        }}
      >
        ← Retour à la liste des offres
      </button>

      {/* Informations d'aide */}
      <div style={{
        marginTop: '30px',
        padding: '15px',
        backgroundColor: '#f8f9fa',
        border: '1px solid #dee2e6',
        borderRadius: '4px',
        fontSize: '14px'
      }}>
        <h4>Informations importantes :</h4>
        <ul>
          <li>Tous les champs marqués d'un * sont obligatoires</li>
          <li>La date limite doit être dans le futur</li>
          <li>La description doit être claire et détaillée</li>
          <li>Une fois créée, l'offre aura le statut "en_attente" jusqu'à validation</li>
        </ul>
      </div>
    </div>
  );
};

export default CreerOffre;
