import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  FaTrash, FaMoon, FaSun, FaUserTie, FaBriefcase,
  FaChartBar, FaBuilding, FaUserShield, FaUser,
  FaBell, FaSearch, FaCalendarAlt, FaMoneyBillWave
} from "react-icons/fa";
import { Bar, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS, CategoryScale, LinearScale, BarElement,
  Title, Tooltip, Legend, ArcElement
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement);

const DashboardMinistere = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [darkMode]);

  const toggleDarkMode = () => setDarkMode(!darkMode);

  // Données simulées pour correspondre à la capture
  const dashboardData = {
    onlineStatus: { value: "$432", label: "Start Back" },
    playSize: { value: "60230", label: "Last Back" },
    returnRate: { value: "60230", label: "Last Back" },
    total: { value: "$642" },
    disabled: { value: "$6468.96" },
    turtle: { value: "1.56k" },
    search: { value: "$3,367.00", label: "Last Back" },
    amount1: { value: "$1989.53", subValue: "$2963.50" },
    recentActivities: { value: "Total Upfront", label: "Last Back" },
    otherStatus: { value: "Select", label: "Last Back" },
    notifications: { value: "Total", label: "Last Back" },
    dates: Array(3).fill({ value: "2022", label: "Last Back" }),
    amounts: Array(3).fill({ value: "$0%", label: "Last Back" })
  };

  return (
    <div className={`min-h-screen ${darkMode ? 'dark:bg-gray-900 dark:text-white' : 'bg-gray-100'}`}>
      {/* Header */}
      <header className={`p-4 ${darkMode ? 'bg-gray-800' : 'bg-white'} shadow-md flex justify-between items-center`}>
        <h1 className="text-2xl font-bold">Fusion</h1>
        <button onClick={toggleDarkMode} className="p-2 rounded-full bg-gray-200 dark:bg-gray-700">
          {darkMode ? <FaSun className="text-yellow-400" /> : <FaMoon />}
        </button>
      </header>

      {/* Main Content */}
      <div className="p-6 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {/* Subboard Section */}
        <div className={`p-6 rounded-lg shadow ${darkMode ? 'bg-gray-800' : 'bg-white'} col-span-1`}>
          <h2 className="text-xl font-bold mb-4">Subboard</h2>
          <ul className="space-y-2">
            {["Trip", "Attention", "Admissions", "Comments", "Center", "Union", "Train", "Time", "Cross", "View", "Documentation"].map((item) => (
              <li key={item} className="hover:bg-gray-100 dark:hover:bg-gray-700 p-2 rounded cursor-pointer">
                {item}
              </li>
            ))}
          </ul>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 col-span-3">
          {/* Online Status */}
          <StatCard 
            icon={<FaUserTie className="text-blue-500" />}
            value={dashboardData.onlineStatus.value}
            label={dashboardData.onlineStatus.label}
            darkMode={darkMode}
          />

          {/* Play Size */}
          <StatCard 
            icon={<FaChartBar className="text-green-500" />}
            value={dashboardData.playSize.value}
            label={dashboardData.playSize.label}
            darkMode={darkMode}
          />

          {/* Return Rate */}
          <StatCard 
            icon={<FaMoneyBillWave className="text-purple-500" />}
            value={dashboardData.returnRate.value}
            label={dashboardData.returnRate.label}
            darkMode={darkMode}
          />

          {/* Total */}
          <StatCard 
            icon={<FaBriefcase className="text-yellow-500" />}
            value={dashboardData.total.value}
            darkMode={darkMode}
          />

          {/* Disabled */}
          <StatCard 
            icon={<FaUserShield className="text-red-500" />}
            value={dashboardData.disabled.value}
            darkMode={darkMode}
          />

          {/* Turtle */}
          <StatCard 
            icon={<FaBuilding className="text-indigo-500" />}
            value={dashboardData.turtle.value}
            darkMode={darkMode}
          />

          {/* Search */}
          <StatCard 
            icon={<FaSearch className="text-pink-500" />}
            value={dashboardData.search.value}
            label={dashboardData.search.label}
            darkMode={darkMode}
          />

          {/* Amount 1 */}
          <div className={`p-4 rounded-lg shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
            <div className="text-gray-500 dark:text-gray-300 text-sm">Amount</div>
            <div className="text-2xl font-bold">{dashboardData.amount1.value}</div>
            <div className="text-gray-500 dark:text-gray-300 text-sm mt-2">{dashboardData.amount1.subValue}</div>
          </div>

          {/* Recent Activities */}
          <StatCard 
            icon={<FaCalendarAlt className="text-orange-500" />}
            value={dashboardData.recentActivities.value}
            label={dashboardData.recentActivities.label}
            darkMode={darkMode}
          />

          {/* Other Status */}
          <StatCard 
            icon={<FaBell className="text-teal-500" />}
            value={dashboardData.otherStatus.value}
            label={dashboardData.otherStatus.label}
            darkMode={darkMode}
          />

          {/* Notifications */}
          <StatCard 
            icon={<FaBell className="text-blue-500" />}
            value={dashboardData.notifications.value}
            label={dashboardData.notifications.label}
            darkMode={darkMode}
          />

          {/* Date Added (x3) */}
          {dashboardData.dates.map((date, index) => (
            <StatCard 
              key={`date-${index}`}
              icon={<FaCalendarAlt className="text-gray-500" />}
              value={date.value}
              label={date.label}
              darkMode={darkMode}
            />
          ))}

          {/* Amounts (x3) */}
          {dashboardData.amounts.map((amount, index) => (
            <StatCard 
              key={`amount-${index}`}
              icon={<FaMoneyBillWave className="text-gray-500" />}
              value={amount.value}
              label={amount.label}
              darkMode={darkMode}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

// Composant de carte statistique réutilisable
const StatCard = ({ icon, value, label, darkMode }) => (
  <div className={`p-4 rounded-lg shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
    <div className="flex justify-between items-start">
      <div className="text-2xl">{icon}</div>
      <div className="text-right">
        <div className="text-2xl font-bold">{value}</div>
        {label && <div className="text-gray-500 dark:text-gray-300 text-sm">{label}</div>}
      </div>
    </div>
  </div>
);

export default DashboardMinistere;
