import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaTrash, FaUser, FaFileAlt, FaClipboardList } from 'react-icons/fa';
import { motion } from 'framer-motion';

const DashboardMinistere = () => {
  const [offres, setOffres] = useState([]);
  const [users, setUsers] = useState([]);
  const [candidatures, setCandidatures] = useState([]);
  const token = localStorage.getItem('token');

  const fetchData = async () => {
    try {
      const [offresRes, usersRes, candidaturesRes] = await Promise.all([
        axios.get('http://localhost:3000/api/offres', { headers: { Authorization: `Bearer ${token}` } }),
        axios.get('http://localhost:3000/api/users', { headers: { Authorization: `Bearer ${token}` } }),
        axios.get('http://localhost:3000/api/candidatures', { headers: { Authorization: `Bearer ${token}` } })
      ]);
      setOffres(offresRes.data);
      setUsers(usersRes.data);
      setCandidatures(candidaturesRes.data);
    } catch (error) {
      console.error("Erreur de chargement des donnÃ©es", error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const deleteUser = async (id) => {
    try {
      await axios.delete(`http://localhost:3000/api/users/${id}`, { headers: { Authorization: `Bearer ${token}` } });
      setUsers(users.filter((u) => u._id !== id));
    } catch (err) {
      console.error('Erreur suppression utilisateur', err);
    }
  };

  const deleteOffre = async (id) => {
    try {
      await axios.delete(`http://localhost:3000/api/offres/${id}`, { headers: { Authorization: `Bearer ${token}` } });
      setOffres(offres.filter((o) => o._id !== id));
    } catch (err) {
      console.error('Erreur suppression offre', err);
    }
  };

  return (
    <div className="p-6 min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-white">
      <h1 className="text-3xl font-bold mb-6">Dashboard MinistÃ¨re</h1>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-10">
        <StatCard icon={<FaFileAlt />} label="Offres" value={offres.length} />
        <StatCard icon={<FaClipboardList />} label="Candidatures" value={candidatures.length} />
        <StatCard icon={<FaUser />} label="Utilisateurs" value={users.length} />
      </div>

      <Section title="Utilisateurs enregistrÃ©s">
        <ItemList items={users} onDelete={deleteUser} itemType="user" />
      </Section>

      <Section title="Offres publiÃ©es">
        <ItemList items={offres} onDelete={deleteOffre} itemType="offre" />
      </Section>
    </div>
  );
};

const StatCard = ({ icon, label, value }) => (
  <div className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-6 flex items-center gap-4">
    <div className="text-3xl text-indigo-500">{icon}</div>
    <div>
      <p className="text-sm uppercase text-gray-500 dark:text-gray-400">{label}</p>
      <p className="text-xl font-bold">{value}</p>
    </div>
  </div>
);

const Section = ({ title, children }) => (
  <div className="mb-10">
    <h2 className="text-2xl font-semibold mb-4">{title}</h2>
    {children}
  </div>
);

const ItemList = ({ items, onDelete, itemType }) => (
  <div className="overflow-x-auto">
    <table className="w-full table-auto border-collapse">
      <thead>
        <tr className="bg-gray-200 dark:bg-gray-700">
          <th className="px-4 py-2 text-left">Nom</th>
          <th className="px-4 py-2 text-left">Email</th>
          <th className="px-4 py-2">Action</th>
        </tr>
      </thead>
      <tbody>
        {items.map((item) => (
          <tr key={item._id} className="border-b dark:border-gray-600">
            <td className="px-4 py-2">{item.nom || item.titre}</td>
            <td className="px-4 py-2">{item.email || item.domaine}</td>
            <td className="px-4 py-2 text-center">
              <button
                onClick={() => onDelete(item._id)}
                className="text-red-600 hover:text-red-800"
              >
                <FaTrash />
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  </div>
);

export default DashboardMinistere;
