import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaCheckCircle, FaTimesCircle, FaChartBar, FaClipboardList } from 'react-icons/fa';

const DashboardMinistere = () => {
  const [offers, setOffers] = useState([]);
  const [offresEnAttente, setOffresEnAttente] = useState([]);
  const [stats, setStats] = useState({ total: 0, actifs: 0 });
  const [message, setMessage] = useState('');

  const token = localStorage.getItem('token');

  useEffect(() => {
    fetchDashboard();
    fetchOffresEnAttente();
  }, []);

  const fetchDashboard = async () => {
    try {
      const res = await axios.get('http://localhost:3000/api/dashboard/ministere', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOffers(res.data.recentOffers);
      setStats(res.data.stats);
    } catch (err) {
      console.error('Erreur chargement dashboard :', err);
    }
  };

  const fetchOffresEnAttente = async () => {
    try {
      const res = await axios.get('http://localhost:3000/api/offres/en-attente', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOffresEnAttente(res.data);
    } catch (err) {
      console.error('Erreur chargement offres en attente :', err);
    }
  };

  const handleValidation = async (id, action) => {
    try {
      await axios.post(`http://localhost:3000/api/ministere/${action}/${id}`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessage(`Offre ${action === 'valider' ? 'validée' : 'rejetée'} avec succès.`);
      fetchDashboard();
      fetchOffresEnAttente();
    } catch (err) {
      console.error('Erreur traitement offre :', err);
    }
  };

  const validerOffre = (id) => handleValidation(id, 'valider');
  const rejeterOffre = (id) => handleValidation(id, 'rejeter');

  return (
    <div className="min-h-screen p-10 bg-gradient-to-r from-blue-900 via-indigo-900 to-purple-900 text-white">
      <h1 className="text-4xl font-bold mb-6 text-center">Espace Ministère</h1>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-10">
        <StatBox icon={<FaChartBar size={30} />} title="Offres totales" value={stats.total} />
        <StatBox icon={<FaCheckCircle size={30} />} title="Offres actives" value={stats.actifs} />
        <StatBox icon={<FaClipboardList size={30} />} title="Offres en attente" value={stats.total - stats.actifs} />
      </div>

      {message && <p className="text-green-300 text-center mb-4">{message}</p>}

      {/* Section Offres en Attente */}
      <h2 className="text-2xl font-semibold mb-4">Offres en Attente de Validation</h2>
      <div className="mb-8">
        {offresEnAttente.map(offer => (
          <div key={offer.id} className="bg-white bg-opacity-10 text-white p-4 mb-3 rounded-lg shadow-md">
            <h3 className="text-xl font-semibold">{offer.title}</h3>
            <p className="text-gray-200">{offer.description}</p>
            <p className="text-sm text-indigo-200">Date limite : {offer.deadline}</p>
            <div className="mt-2 flex justify-end gap-4">
              <button
                className="bg-green-600 hover:bg-green-700 text-white px-4 py-1 rounded text-sm"
                onClick={() => validerOffre(offer.id)}
              >
                Valider
              </button>
              <button
                className="bg-red-600 hover:bg-red-700 text-white px-4 py-1 rounded text-sm"
                onClick={() => rejeterOffre(offer.id)}
              >
                Rejeter
              </button>
            </div>
          </div>
        ))}
      </div>

      <h2 className="text-2xl font-semibold mb-4">Dernières Offres à traiter</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {offers.map((offer) => (
          <div key={offer.id} className="bg-white bg-opacity-10 p-4 rounded-xl shadow-md">
            <h3 className="text-lg font-bold">{offer.title}</h3>
            <p className="text-sm mb-2">{offer.description}</p>
            <p className="text-xs text-indigo-200 mb-4">Date limite : {offer.deadline}</p>
            <div className="flex justify-end gap-4">
              <button
                onClick={() => handleValidation(offer.id, 'valider')}
                className="px-4 py-1 bg-green-600 rounded hover:bg-green-700 text-sm"
              >
                Valider
              </button>
              <button
                onClick={() => handleValidation(offer.id, 'rejeter')}
                className="px-4 py-1 bg-red-600 rounded hover:bg-red-700 text-sm"
              >
                Rejeter
              </button>
            </div>
          </div>
        ))}
      </div>

      <footer className="mt-16 text-center text-sm text-gray-300 italic">
        Statistiques générées automatiquement – Ministère des Finances © 2025
      </footer>
    </div>
  );
};

const StatBox = ({ icon, title, value }) => (
  <div className="bg-white bg-opacity-10 p-6 rounded-xl shadow-md text-center">
    <div className="text-indigo-300 mb-2">{icon}</div>
    <h3 className="text-lg font-semibold">{title}</h3>
    <p className="text-2xl font-bold">{value}</p>
  </div>
);

export default DashboardMinistere;
