import React, { useState } from 'react';
import { motion } from 'framer-motion';
import {
  FaSave,
  FaTimes,
  FaCalendarAlt,
  FaMoneyBillWave,
  FaMapMarkerAlt
} from 'react-icons/fa';

const CreerOffre = () => {
  const [formData, setFormData] = useState({
    titre: '',
    description: '',
    dateLimite: '',
    salaire: '',
    lieu: '',
    typeContrat: 'CDI'
  });

  const [darkMode, setDarkMode] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Offre cr√©√©e:', formData);
  };

  return (
    <div className={`min-h-screen transition-colors duration-500 ${darkMode ? 'bg-gray-900 text-gray-100' : 'bg-gray-50 text-gray-900'}`}>
      <div className="container mx-auto px-4 py-10">
        {/* Header */}
        <div className="flex justify-between items-center mb-8">
          <motion.h1
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5 }}
            className="text-4xl font-bold"
          >
            Cr√©er une nouvelle offre
          </motion.h1>
          <button
            onClick={() => setDarkMode(!darkMode)}
            className={`px-4 py-2 rounded-lg font-semibold shadow-md hover:scale-105 transition-all duration-300 ${
              darkMode ? 'bg-yellow-400 text-gray-900' : 'bg-gray-800 text-white'
            }`}
          >
            {darkMode ? '‚òÄÔ∏è Mode Clair' : 'üåô Mode Sombre'}
          </button>
        </div>

        {/* Form Card */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className={`p-8 rounded-2xl shadow-xl border transition-all duration-500 ${
            darkMode
              ? 'bg-gray-800 border-gray-700'
              : 'bg-white border-gray-200'
          }`}
        >
          <form onSubmit={handleSubmit}>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              {/* Colonne gauche */}
              <div className="space-y-6">
                {/* Titre */}
                <div>
                  <label className="block mb-2 font-semibold">Titre du poste</label>
                  <input
                    type="text"
                    name="titre"
                    value={formData.titre}
                    onChange={handleChange}
                    placeholder="Ex: D√©veloppeur Full Stack"
                    className={`w-full p-3 rounded-lg shadow-sm focus:ring-2 focus:outline-none transition ${
                      darkMode
                        ? 'bg-gray-700 border border-gray-600 focus:ring-yellow-400'
                        : 'bg-gray-100 border border-gray-300 focus:ring-blue-400'
                    }`}
                    required
                  />
                </div>

                {/* Description */}
                <div>
                  <label className="block mb-2 font-semibold">Description</label>
                  <textarea
                    name="description"
                    value={formData.description}
                    onChange={handleChange}
                    rows="6"
                    placeholder="D√©crivez les missions, les comp√©tences requises, etc."
                    className={`w-full p-3 rounded-lg shadow-sm focus:ring-2 focus:outline-none transition ${
                      darkMode
                        ? 'bg-gray-700 border border-gray-600 focus:ring-yellow-400'
                        : 'bg-gray-100 border border-gray-300 focus:ring-blue-400'
                    }`}
                    required
                  />
                </div>
              </div>

              {/* Colonne droite */}
              <div className="space-y-6">
                {/* Date limite */}
                <div>
                  <label className="flex items-center mb-2 font-semibold gap-2">
                    <FaCalendarAlt className="text-blue-400" />
                    Date limite
                  </label>
                  <input
                    type="date"
                    name="dateLimite"
                    value={formData.dateLimite}
                    onChange={handleChange}
                    className={`w-full p-3 rounded-lg shadow-sm focus:ring-2 focus:outline-none transition ${
                      darkMode
                        ? 'bg-gray-700 border border-gray-600 focus:ring-yellow-400'
                        : 'bg-gray-100 border border-gray-300 focus:ring-blue-400'
                    }`}
                    required
                  />
                </div>

                {/* Salaire */}
                <div>
                  <label className="flex items-center mb-2 font-semibold gap-2">
                    <FaMoneyBillWave className="text-green-400" />
                    Salaire (optionnel)
                  </label>
                  <input
                    type="text"
                    name="salaire"
                    value={formData.salaire}
                    onChange={handleChange}
                    placeholder="Ex: 2500‚Ç¨ / mois"
                    className={`w-full p-3 rounded-lg shadow-sm focus:ring-2 focus:outline-none transition ${
                      darkMode
                        ? 'bg-gray-700 border border-gray-600 focus:ring-yellow-400'
                        : 'bg-gray-100 border border-gray-300 focus:ring-blue-400'
                    }`}
                  />
                </div>

                {/* Lieu */}
                <div>
                  <label className="flex items-center mb-2 font-semibold gap-2">
                    <FaMapMarkerAlt className="text-red-400" />
                    Lieu
                  </label>
                  <input
                    type="text"
                    name="lieu"
                    value={formData.lieu}
                    onChange={handleChange}
                    placeholder="Ex: Paris, Lyon, Dakar..."
                    className={`w-full p-3 rounded-lg shadow-sm focus:ring-2 focus:outline-none transition ${
                      darkMode
                        ? 'bg-gray-700 border border-gray-600 focus:ring-yellow-400'
                        : 'bg-gray-100 border border-gray-300 focus:ring-blue-400'
                    }`}
                  />
                </div>

                {/* Type de contrat */}
                <div>
                  <label className="block mb-2 font-semibold">Type de contrat</label>
                  <select
                    name="typeContrat"
                    value={formData.typeContrat}
                    onChange={handleChange}
                    className={`w-full p-3 rounded-lg shadow-sm focus:ring-2 focus:outline-none transition ${
                      darkMode
                        ? 'bg-gray-700 border border-gray-600 focus:ring-yellow-400'
                        : 'bg-gray-100 border border-gray-300 focus:ring-blue-400'
                    }`}
                  >
                    <option value="CDI">CDI</option>
                    <option value="CDD">CDD</option>
                    <option value="Stage">Stage</option>
                    <option value="Freelance">Freelance</option>
                  </select>
                </div>
              </div>
            </div>

            {/* Boutons */}
            <div className="flex justify-end gap-4 mt-10">
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                type="button"
                className="flex items-center px-6 py-3 bg-red-500 text-white rounded-lg hover:bg-red-600 transition"
              >
                <FaTimes className="mr-2" />
                Annuler
              </motion.button>
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                type="submit"
                className="flex items-center px-6 py-3 bg-green-500 text-white rounded-lg hover:bg-green-600 transition"
              >
                <FaSave className="mr-2" />
                Publier l'offre
              </motion.button>
            </div>
          </form>
        </motion.div>
      </div>
    </div>
  );
};

export default CreerOffre;
