// src/Pages/DashboardEntreprise.jsx
import React, { useEffect, useState } from 'react';

const DashboardEntreprise = () => {
  const [offers, setOffers] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem('token');
    fetch('http://localhost:3000/api/dashboard/entreprise', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => setOffers(data.offers || []));
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold text-blue-700 mb-4">Dashboard Entreprise</h1>
      {offers.map((offer) => (
        <div key={offer.id} className="p-4 mb-3 bg-gray-100 rounded-lg shadow">
          <h3 className="font-semibold">{offer.title}</h3>
          <p className="text-sm text-gray-600">{offer.description}</p>
        </div>
      ))}
    </div>
  );
};

export default DashboardEntreprise;
