import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';  // Import du hook useAuth

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3000';
const ROUTES = {
  MINISTERE: '/ministere/home',
  ENTREPRISE: '/entreprise/home',
  DEFAULT: '/',
  REGISTER: '/register'
};

const Login = () => {
  const [formData, setFormData] = useState({ username: '', password: '' });
  const [uiState, setUiState] = useState({
    showPassword: false,
    error: '',
    success: '',
    isMobile: false,
    isLoading: false
  });

  const { login } = useAuth(); // Utilisation du contexte Auth
  const navigate = useNavigate();

  useEffect(() => {
    const checkScreenSize = () => {
      setUiState(prev => ({
        ...prev,
        isMobile: window.innerWidth < 768
      }));
    };
    checkScreenSize();
    window.addEventListener('resize', checkScreenSize);
    return () => window.removeEventListener('resize', checkScreenSize);
  }, []);

  const handleChange = useCallback((e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  }, []);

  const handleLogin = useCallback(async (e) => {
    e.preventDefault();
    setUiState(prev => ({ ...prev, error: '', success: '', isLoading: true }));

    try {
      const response = await axios.post(`${API_BASE_URL}/api/auth/login`, formData);
      const { token, user } = response.data;

      // Utiliser la fonction login du contexte, qui stocke le token et redirige
      await login({ token, ...user });

      setUiState(prev => ({
        ...prev,
        success: `Connecté avec succès en tant que ${user.role}`,
        isLoading: false
      }));
      // Plus besoin de navigate ici, c'est géré dans login()

    } catch (error) {
      const errorMessage = error.response?.data?.error || error.message || 'Échec de connexion';
      setUiState(prev => ({ ...prev, error: errorMessage, isLoading: false }));
    }
  }, [formData, login]);

  const togglePasswordVisibility = useCallback(() => {
    setUiState(prev => ({ ...prev, showPassword: !prev.showPassword }));
  }, []);

  // Les icônes et spinner sont les mêmes, tu peux les garder

  return (
    <div className={`flex min-h-screen items-center justify-center bg-white p-4 ${uiState.isMobile ? 'pt-16' : ''}`}>
      <form
        onSubmit={handleLogin}
        className={`bg-white p-6 rounded-2xl shadow-lg w-full ${uiState.isMobile ? 'max-w-xs' : 'max-w-md'} text-gray-800`}
        noValidate
      >
        <h2 className={`${uiState.isMobile ? 'text-2xl' : 'text-3xl'} font-semibold mb-6 text-center text-sky-600`}>
          Connexion
        </h2>

        {uiState.error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4 text-sm">
            {uiState.error}
          </div>
        )}

        {uiState.success && (
          <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4 text-sm">
            {uiState.success}
          </div>
        )}

        <div className="mb-4">
          <label htmlFor="username" className="block text-sm font-medium mb-1 text-gray-700">
            Nom d'utilisateur
          </label>
          <input
            type="text"
            id="username"
            name="username"
            value={formData.username}
            onChange={handleChange}
            placeholder="Entrez votre nom d'utilisateur"
            required
            autoComplete="username"
            className="w-full p-2 rounded bg-gray-50 border border-gray-300 focus:outline-none focus:ring-1 focus:ring-sky-400 text-sm"
          />
        </div>

        <div className="mb-6">
          <label htmlFor="password" className="block text-sm font-medium mb-1 text-gray-700">
            Mot de passe
          </label>
          <div className="relative">
            <input
              type={uiState.showPassword ? "text" : "password"}
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Entrez votre mot de passe"
              required
              autoComplete="current-password"
              className="w-full p-2 pr-10 rounded bg-gray-50 border border-gray-300 focus:outline-none focus:ring-1 focus:ring-sky-400 text-sm"
            />
            <button
              type="button"
              onClick={togglePasswordVisibility}
              className="absolute inset-y-0 right-0 flex items-center pr-3 text-gray-500 hover:text-gray-700 focus:outline-none"
              aria-label={uiState.showPassword ? "Masquer le mot de passe" : "Afficher le mot de passe"}
            >
              {uiState.showPassword ? (
                /* Icone "eye-off" */
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13.875 18.825A10.05 10.05 0 0112 19c-4.478 0-8.268-2.943-9.543-7a9.97 9.97 0 011.563-3.029m5.858.908a3 3 0 114.243 4.243M9.878 9.878l4.242 4.242M9.88 9.88l-3.29-3.29m7.532 7.532l3.29 3.29M3 3l3.59 3.59m0 0A9.953 9.953 0 0112 5c4.478 0 8.268 2.943 9.543 7a10.025 10.025 0 01-4.132 5.411m0 0L21 21" />
                </svg>
              ) : (
                /* Icone "eye" */
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
                </svg>
              )}
            </button>
          </div>
        </div>

        <button
          type="submit"
          disabled={uiState.isLoading}
          className={`w-full bg-sky-600 hover:bg-sky-700 text-white font-medium py-2 px-4 rounded transition duration-300 flex items-center justify-center ${
            uiState.isLoading ? 'opacity-75 cursor-not-allowed' : ''
          }`}
        >
          {uiState.isLoading ? (
            <>
              <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
              </svg>
              Connexion...
            </>
          ) : 'Se connecter'}
        </button>

        <div className="text-center mt-4 text-sm">
          <p className="text-gray-600">
            Pas encore de compte ?{' '}
            <Link
              to={ROUTES.REGISTER}
              className="text-sky-600 hover:text-sky-800 underline transition duration-200"
            >
              S'inscrire
            </Link>
          </p>
        </div>
      </form>
    </div>
  );
};

export default Login;
