import React, { useEffect, useState } from 'react';

const MesCandidatures = () => {
  const [candidatures, setCandidatures] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('Utilisateur non authentifié');
      setLoading(false);
      return;
    }

    fetch('http://localhost:3000/api/mes-candidatures', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => {
        if (!res.ok) throw new Error('Erreur lors du chargement des candidatures');
        return res.json();
      })
      .then(data => {
        setCandidatures(data);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  }, []);

  if (loading) return <div className="p-4">Chargement...</div>;
  if (error) return <div className="p-4 text-red-600">{error}</div>;

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h2 className="text-2xl font-bold mb-4">Mes Candidatures</h2>
      {candidatures.length === 0 ? (
        <p className="text-gray-500">Aucune candidature envoyée.</p>
      ) : (
        <ul className="space-y-6">
          {candidatures.map(c => (
            <li key={c.id} className="border p-6 rounded shadow bg-white">
              <p className="font-semibold text-indigo-600 text-lg">Offre : {c.offerTitle || c.offerId}</p>
              <p className="text-gray-700 mt-2">Entreprise : {c.companyName || 'Non renseigné'}</p>
              <p className="text-gray-700 mt-2">Message : {c.message || 'Aucun message'}</p>
              {c.fileUrl && (
                <p className="mt-2">
                  <a
                    href={`http://localhost:3000${c.fileUrl}`}
                    target="_blank"
                    rel="noreferrer"
                    className="text-blue-600 underline"
                  >
                    Télécharger le fichier joint
                  </a>
                </p>
              )}
              <p className="text-sm text-gray-500 mt-3">Envoyée le {new Date(c.date).toLocaleString()}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default MesCandidatures;
