import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaPaperPlane, FaSpinner, FaCheck, FaTimes, FaFilePdf, FaFileWord, FaFileImage } from 'react-icons/fa';
import { useParams, useNavigate } from 'react-router-dom';

const Postuler = () => {
  const { offerId } = useParams(); // Récupère l'ID de l'offre depuis l'URL
  const navigate = useNavigate();
  const [offer, setOffer] = useState(null);
  const [file, setFile] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [errors, setErrors] = useState({});
  const [filePreview, setFilePreview] = useState(null);
  const [message, setMessage] = useState('');

  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchOfferDetails = async () => {
      try {
        const response = await axios.get(`/api/entreprise/offres/${offerId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setOffer(response.data);
      } catch (err) {
        setMessage('Erreur lors du chargement des détails de l\'offre');
        console.error(err);
      }
    };

    fetchOfferDetails();
  }, [offerId, token]);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      // Vérification de la taille du fichier (max 5MB)
      if (selectedFile.size > 5 * 1024 * 1024) {
        setMessage('Le fichier ne doit pas dépasser 5MB');
        return;
      }
      
      setFile(selectedFile);
      setErrors({...errors, file: null});
      
      // Créer une prévisualisation
      const reader = new FileReader();
      reader.onloadend = () => {
        setFilePreview({
          name: selectedFile.name,
          type: selectedFile.type,
          size: selectedFile.size,
          url: reader.result
        });
      };
      reader.readAsDataURL(selectedFile);
    }
  };

  const getFileIcon = (fileType) => {
    if (fileType.includes('pdf')) return <FaFilePdf className="text-red-500 text-2xl" />;
    if (fileType.includes('word') || fileType.includes('msword')) return <FaFileWord className="text-blue-500 text-2xl" />;
    if (fileType.includes('image')) return <FaFileImage className="text-green-500 text-2xl" />;
    return <FaFilePdf className="text-gray-500 text-2xl" />;
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!file) {
      setMessage('Veuillez sélectionner un fichier');
      return;
    }
    
    setIsLoading(true);
    setIsSubmitted(false);

    const formData = new FormData();
    formData.append('offerId', offerId);
    formData.append('file', file);
    formData.append('status', 'pending'); // Statut initial pour le ministère/admin
    formData.append('viewedByMinistere', false);
    formData.append('viewedByAdmin', false);

    try {
      await axios.post('/api/candidatures', formData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });
      
      setMessage('Candidature soumise avec succès !');
      setIsSubmitted(true);
      setFile(null);
      setFilePreview(null);
      
      // Redirection après 2 secondes
      setTimeout(() => {
        navigate('/mes-candidatures');
      }, 2000);
    } catch (error) {
      console.error(error);
      setMessage(error.response?.data?.message || 'Erreur lors de la soumission de la candidature');
    } finally {
      setIsLoading(false);
    }
  };

  if (!offer) {
    return <div className="p-8 text-center">Chargement de l'offre...</div>;
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8 px-4">
      <div className="max-w-2xl mx-auto bg-white rounded-xl shadow-md overflow-hidden">
        <div className="p-8">
          <button 
            onClick={() => navigate(-1)}
            className="mb-4 text-blue-600 hover:text-blue-800 font-medium"
          >
            &larr; Retour à l'offre
          </button>

          <div className="flex items-center justify-between mb-6">
            <h2 className="text-2xl font-bold text-gray-800">Postuler à l'offre: {offer.title}</h2>
            {isSubmitted && (
              <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-green-100 text-green-800">
                <FaCheck className="mr-1" /> Soumis
              </span>
            )}
          </div>

          {message && (
            <div className={`mb-4 p-3 rounded-md ${
              message.includes('succès') ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'
            }`}>
              {message}
            </div>
          )}

          <div className="mb-6 p-4 bg-blue-50 rounded-lg">
            <h3 className="font-medium text-blue-800">Informations sur l'offre</h3>
            <p className="mt-1 text-sm text-gray-600">{offer.description}</p>
            <p className="mt-2 text-sm"><span className="font-medium">Entreprise:</span> {offer.company}</p>
            <p className="text-sm"><span className="font-medium">Date limite:</span> {new Date(offer.deadline).toLocaleDateString()}</p>
          </div>

          <form onSubmit={handleSubmit} className="space-y-6">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Votre CV/Lettre de motivation <span className="text-red-500">*</span>
              </label>
              
              {filePreview ? (
                <div className="border border-gray-300 rounded-md p-4 flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    {getFileIcon(filePreview.type)}
                    <div>
                      <p className="text-sm font-medium text-gray-900 truncate max-w-xs">{filePreview.name}</p>
                      <p className="text-xs text-gray-500">{formatFileSize(filePreview.size)}</p>
                    </div>
                  </div>
                  <button
                    type="button"
                    onClick={() => {
                      setFile(null);
                      setFilePreview(null);
                    }}
                    className="text-red-500 hover:text-red-700"
                  >
                    <FaTimes />
                  </button>
                </div>
              ) : (
                <div className="flex items-center justify-center w-full">
                  <label
                    htmlFor="file-upload"
                    className={`flex flex-col items-center justify-center w-full h-32 border-2 border-dashed rounded-lg cursor-pointer ${
                      errors.file ? 'border-red-500 bg-red-50' : 'border-gray-300 hover:border-blue-500'
                    }`}
                  >
                    <div className="flex flex-col items-center justify-center pt-5 pb-6">
                      <FaPaperPlane className="w-8 h-8 mb-3 text-gray-400" />
                      <p className="mb-2 text-sm text-gray-500">
                        <span className="font-semibold">Cliquez pour uploader</span> ou glissez-déposez
                      </p>
                      <p className="text-xs text-gray-500">PDF, DOCX, JPG (Max. 5MB)</p>
                    </div>
                    <input
                      id="file-upload"
                      type="file"
                      onChange={handleFileChange}
                      className="hidden"
                      accept=".pdf,.doc,.docx,.jpg,.jpeg,.png"
                    />
                  </label>
                </div>
              )}
            </div>

            <div className="pt-2">
              <button
                type="submit"
                disabled={isLoading || isSubmitted}
                className={`w-full flex justify-center items-center py-3 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white ${
                  isLoading ? 'bg-blue-400' : 'bg-blue-600 hover:bg-blue-700'
                } focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 ${
                  isLoading ? 'opacity-75 cursor-not-allowed' : ''
                }`}
              >
                {isLoading ? (
                  <>
                    <FaSpinner className="animate-spin mr-2" />
                    Envoi en cours...
                  </>
                ) : isSubmitted ? (
                  <>
                    <FaCheck className="mr-2" />
                    Candidature envoyée
                  </>
                ) : (
                  <>
                    <FaPaperPlane className="mr-2" />
                    Soumettre ma candidature
                  </>
                )}
              </button>
            </div>
          </form>

          <div className="mt-6 border-t border-gray-200 pt-4">
            <h3 className="text-sm font-medium text-gray-900">Processus de validation</h3>
            <ul className="mt-2 text-sm text-gray-600 space-y-1">
              <li>• Votre candidature sera examinée par le ministère</li>
              <li>• L'administrateur validera votre dossier</li>
              <li>• Vous recevrez une notification par email</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Postuler;
