import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaUsers, FaFileAlt, FaTrash, FaBullseye } from 'react-icons/fa';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';

const DashboardMinistere = () => {
  const [users, setUsers] = useState([]);
  const [offers, setOffers] = useState([]);
  const [applications, setApplications] = useState([]);

  useEffect(() => {
    fetchUsers();
    fetchOffers();
    fetchApplications();
  }, []);

  const fetchUsers = async () => {
    try {
      const res = await axios.get('/api/users');
      setUsers(res.data);
    } catch (err) {
      console.error('Erreur lors du chargement des utilisateurs', err);
    }
  };

  const fetchOffers = async () => {
    try {
      const res = await axios.get('/api/offres');
      setOffers(res.data);
    } catch (err) {
      console.error('Erreur lors du chargement des offres', err);
    }
  };

  const fetchApplications = async () => {
    try {
      const res = await axios.get('/api/candidatures');
      setApplications(res.data);
    } catch (err) {
      console.error('Erreur lors du chargement des candidatures', err);
    }
  };

  const deleteUser = async (id) => {
    try {
      await axios.delete(`/api/users/${id}`);
      fetchUsers();
    } catch (err) {
      console.error('Erreur lors de la suppression de lutilisateur', err);
    }
  };

  const deleteOffer = async (id) => {
    try {
      await axios.delete(`/api/offres/${id}`);
      fetchOffers();
    } catch (err) {
      console.error("Erreur lors de la suppression de l'offre", err);
    }
  };

  return (
    <div className="p-8 dark:bg-gray-900 min-h-screen">
      <h1 className="text-3xl font-bold mb-8 text-center text-blue-800 dark:text-white">Dashboard Ministère</h1>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-12">
        <div className="bg-white dark:bg-gray-800 rounded-xl p-6 shadow">
          <FaUsers className="text-3xl text-indigo-600 dark:text-indigo-400 mb-2" />
          <p className="text-gray-600 dark:text-gray-300">Utilisateurs enregistrés</p>
          <h2 className="text-2xl font-bold text-blue-800 dark:text-white">{users.length}</h2>
        </div>

        <div className="bg-white dark:bg-gray-800 rounded-xl p-6 shadow">
          <FaFileAlt className="text-3xl text-green-600 dark:text-green-400 mb-2" />
          <p className="text-gray-600 dark:text-gray-300">Offres publiées</p>
          <h2 className="text-2xl font-bold text-blue-800 dark:text-white">{offers.length}</h2>
        </div>

        <div className="bg-white dark:bg-gray-800 rounded-xl p-6 shadow">
          <FaBullseye className="text-3xl text-yellow-600 dark:text-yellow-400 mb-2" />
          <p className="text-gray-600 dark:text-gray-300">Candidatures reçues</p>
          <h2 className="text-2xl font-bold text-blue-800 dark:text-white">{applications.length}</h2>
        </div>
      </div>

      <div className="mb-12">
        <h2 className="text-xl font-semibold text-gray-700 dark:text-white mb-4">Liste des Utilisateurs</h2>
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white dark:bg-gray-800 rounded-xl">
            <thead>
              <tr className="text-left text-gray-600 dark:text-gray-300">
                <th className="p-4">Nom</th>
                <th className="p-4">Email</th>
                <th className="p-4">Rôle</th>
                <th className="p-4">Action</th>
              </tr>
            </thead>
            <tbody>
              {users.map(user => (
                <tr key={user.id} className="border-t dark:border-gray-700">
                  <td className="p-4 text-gray-800 dark:text-gray-200">{user.username}</td>
                  <td className="p-4 text-gray-800 dark:text-gray-200">{user.email}</td>
                  <td className="p-4 text-gray-800 dark:text-gray-200">{user.role}</td>
                  <td className="p-4">
                    <button
                      onClick={() => deleteUser(user.id)}
                      className="text-red-600 hover:text-red-800 dark:text-red-400"
                    >
                      <FaTrash />
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      <div>
        <h2 className="text-xl font-semibold text-gray-700 dark:text-white mb-4">Liste des Offres</h2>
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white dark:bg-gray-800 rounded-xl">
            <thead>
              <tr className="text-left text-gray-600 dark:text-gray-300">
                <th className="p-4">Titre</th>
                <th className="p-4">Statut</th>
                <th className="p-4">Date</th>
                <th className="p-4">Action</th>
              </tr>
            </thead>
            <tbody>
              {offers.map(offer => (
                <tr key={offer.id} className="border-t dark:border-gray-700">
                  <td className="p-4 text-gray-800 dark:text-gray-200">{offer.titre}</td>
                  <td className="p-4 text-gray-800 dark:text-gray-200">{offer.statut}</td>
                  <td className="p-4 text-gray-800 dark:text-gray-200">{new Date(offer.date).toLocaleDateString()}</td>
                  <td className="p-4">
                    <button
                      onClick={() => deleteOffer(offer.id)}
                      className="text-red-600 hover:text-red-800 dark:text-red-400"
                    >
                      <FaTrash />
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default DashboardMinistere;
