import React, { useEffect, useState } from 'react';
import { FaBullseye, FaFileAlt, FaUsers, FaMobileAlt, FaSearch } from 'react-icons/fa';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Home = () => {
  const navigate = useNavigate();
  const [offers, setOffers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const fetchPublishedOffers = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/offres/publiees');
        setOffers(response.data);
      } catch (error) {
        console.error("Erreur lors du chargement des offres", error);
      } finally {
        setLoading(false);
      }
    };

    fetchPublishedOffers();
  }, []);

  const filteredOffers = offers.filter(offer => 
    offer.title.toLowerCase().includes(searchTerm.toLowerCase()) || 
    offer.description.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="min-h-screen bg-gradient-to-r from-indigo-900 via-purple-900 to-pink-900 text-white flex flex-col">
      {/* Header */}
      <header className="p-8 text-center mt-6">
        <h1 className="text-5xl font-extrabold mb-4 animate-fadeInDown">
          Plateforme des Marchés Publics
        </h1>
        <p className="text-xl max-w-3xl mx-auto animate-fadeInUp">
          Garantir la transparence, la confiance et l'efficacité dans la gestion des marchés publics.
        </p>
      </header>

      {/* Barre de recherche */}
      <div className="px-6 md:px-20 mt-8 mb-6">
        <div className="relative max-w-2xl mx-auto">
          <FaSearch className="absolute left-4 top-1/2 transform -translate-y-1/2 text-gray-300" />
          <input
            type="text"
            placeholder="Rechercher une offre..."
            className="w-full pl-12 pr-4 py-3 bg-white bg-opacity-20 rounded-full text-white placeholder-gray-300 focus:outline-none focus:ring-2 focus:ring-indigo-300"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
      </div>

      {/* Résumé des appels d'offres */}
      <section className="mt-8 px-6 md:px-20">
        <h2 className="text-3xl font-bold mb-6 text-center">Derniers Appels d'Offres</h2>
        
        {loading ? (
          <div className="flex justify-center items-center h-40">
            <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-white"></div>
          </div>
        ) : filteredOffers.length === 0 ? (
          <div className="text-center py-12">
            <p className="text-xl text-gray-300">
              {searchTerm ? "Aucune offre ne correspond à votre recherche" : "Aucune offre disponible pour le moment"}
            </p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredOffers.map((offer, index) => (
              <OfferCard 
                key={offer._id || index} 
                offer={offer} 
                onClick={() => navigate(`/offre/${offer._id}`)}
              />
            ))}
          </div>
        )}
      </section>

      {/* Section objectifs */}
      <section className="flex flex-col md:flex-row justify-around mt-16 px-6 md:px-20 gap-6 mb-16">
        <Objective
          icon={<FaBullseye size={48} />}
          title="Objectif Clair"
          description="Découvrez nos missions et notre vision."
          link="/objectif"
        />
        <Objective
          icon={<FaFileAlt size={48} />}
          title="Toutes les Offres"
          description="Consultez la liste complète des appels d'offres."
          link="/offres"
        />
        <Objective
          icon={<FaUsers size={48} />}
          title="Espace Entreprise"
          description="Accédez à votre espace pour postuler aux offres."
          link="/entreprise"
        />
      </section>

      {/* Footer */}
      <footer className="bg-gradient-to-r from-indigo-900 via-purple-900 to-pink-900 text-white py-10 mt-auto">
        <div className="max-w-5xl mx-auto px-4 text-center">
          <h2 className="text-2xl font-semibold mb-4">Plateforme de Transparence des Marchés Publics</h2>
          
          <p className="text-sm text-gray-200 mb-4">
            Nous croyons en une gouvernance plus juste, plus ouverte et plus responsable.
          </p>

          <div className="flex justify-center gap-6 text-indigo-200 text-xl mb-6">
            <a href="#" aria-label="Facebook" className="hover:text-white"><i className="fab fa-facebook-f"></i></a>
            <a href="#" aria-label="Twitter" className="hover:text-white"><i className="fab fa-twitter"></i></a>
            <a href="#" aria-label="LinkedIn" className="hover:text-white"><i className="fab fa-linkedin-in"></i></a>
          </div>

          <p className="text-xs text-gray-300">© {new Date().getFullYear()} Ministère des Finances – Tous droits réservés</p>
        </div>
      </footer>

      {/* Animations CSS */}
      <style>{`
        @keyframes fadeInDown {
          0% {opacity: 0; transform: translateY(-30px);}
          100% {opacity: 1; transform: translateY(0);}
        }
        @keyframes fadeInUp {
          0% {opacity: 0; transform: translateY(30px);}
          100% {opacity: 1; transform: translateY(0);}
        }
        .animate-fadeInDown {
          animation: fadeInDown 1s ease forwards;
        }
        .animate-fadeInUp {
          animation: fadeInUp 1.2s ease forwards;
        }
      `}</style>
    </div>
  );
};

// Composant Carte d'Offre
const OfferCard = ({ offer, onClick }) => (
  <div 
    onClick={onClick}
    className="bg-white bg-opacity-10 rounded-xl p-6 hover:bg-opacity-20 transition-all cursor-pointer shadow-md hover:shadow-lg"
  >
    <div className="flex justify-between items-start mb-3">
      <h3 className="text-lg font-semibold line-clamp-2">{offer.title}</h3>
      {offer.statut === 'urgent' && (
        <span className="bg-red-500 text-white text-xs px-2 py-1 rounded-full">Urgent</span>
      )}
    </div>
    <p className="text-gray-300 text-sm mb-4 line-clamp-3">{offer.description}</p>
    <div className="flex justify-between items-center text-xs">
      <span className="text-indigo-300">Date limite: {new Date(offer.deadline).toLocaleDateString()}</span>
      <span className="text-gray-400">Ref: {offer.reference}</span>
    </div>
  </div>
);

// Composant Objectif
const Objective = ({ icon, title, description, link }) => (
  <Link 
    to={link} 
    className="bg-white bg-opacity-10 rounded-2xl p-6 flex flex-col items-center text-center max-w-xs hover:scale-105 transition-transform duration-300 shadow-lg hover:bg-opacity-20 flex-1"
  >
    <div className="mb-4 text-indigo-300">{icon}</div>
    <h3 className="text-xl font-semibold mb-2">{title}</h3>
    <p className="text-gray-300 text-sm">{description}</p>
  </Link>
);

export default Home;
