import React from 'react';
import { FaBullseye, FaFileAlt, FaUsers, FaMobileAlt } from 'react-icons/fa';
import { Link, useNavigate } from 'react-router-dom';

const Home = () => {
  const navigate = useNavigate();

  return (
    <div className="min-h-screen bg-gradient-to-r from-indigo-900 via-purple-900 to-pink-900 text-white flex flex-col">
      
      {/* Header */}
      <header className="relative p-8 text-center">
        {/* Navigation en haut à droite */}
        <div className="absolute top-6 right-8 flex gap-6 text-sm">
          <Link to="/offres" className="hover:underline">Offres</Link>
          <Link to="/candidature" className="hover:underline">Candidature</Link>
          <Link to="/contact" className="hover:underline">Contact</Link>
          <Link to="/login" className="hover:underline font-semibold">Connexion</Link>
        </div>

        <h1 className="text-5xl font-extrabold mb-4 animate-fadeInDown">
          Plateforme de Transparence des Marchés Publics
        </h1>
        <p className="text-xl max-w-3xl mx-auto animate-fadeInUp">
          Garantir la transparence, la confiance et l'efficacité dans la gestion des marchés publics.
        </p>
      </header>

      {/* Résumé des appels d’offres */}
      <section className="mt-12 px-6 md:px-20">
        <h2 className="text-3xl font-bold mb-6 text-center">Derniers Appels d’Offres</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {offers.map((offer, index) => (
            <div key={index} className="bg-white bg-opacity-10 rounded-xl p-4 hover:bg-opacity-20 transition shadow-md">
              <h3 className="text-lg font-semibold mb-2">{offer.title}</h3>
              <p className="text-gray-300 text-sm mb-2">{offer.description}</p>
              <p className="text-indigo-300 text-xs">Date limite : {offer.deadline}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Section objectifs */}
      <section className="flex flex-col md:flex-row justify-around mt-16 px-6 md:px-20 gap-12">
        <Objective
          icon={<FaBullseye size={48} />}
          title="Objectif Clair"
          description="Découvrez nos missions et notre vision."
          link="/objectif"
        />
        <Objective
          icon={<FaFileAlt size={48} />}
          title="Appels d'Offres"
          description="Consultez la liste des appels d’offres disponibles."
          link="/offres"
        />
        <Objective
          icon={<FaUsers size={48} />}
          title="Participation"
          description="Soumettez votre candidature à une offre."
          link="/candidature"
        />
        <Objective
          icon={<FaMobileAlt size={48} />}
          title="Accessibilité"
          description="Découvrez comment la plateforme est accessible à tous."
          link="/accessibilite"
        />
      </section>

      {/* Footer */}
      <footer className="mt-auto p-6 text-center text-gray-300 text-sm border-t border-white/20">
        <p>© 2025 copywrite@Arotoky - Tous droits réservés</p>
      </footer>

      {/* Animations CSS */}
      <style>{`
        @keyframes fadeInDown {
          0% {opacity: 0; transform: translateY(-30px);}
          100% {opacity: 1; transform: translateY(0);}
        }
        @keyframes fadeInUp {
          0% {opacity: 0; transform: translateY(30px);}
          100% {opacity: 1; transform: translateY(0);}
        }
        .animate-fadeInDown {
          animation: fadeInDown 1s ease forwards;
        }
        .animate-fadeInUp {
          animation: fadeInUp 1.2s ease forwards;
        }
      `}</style>
    </div>
  );
};

// Carte d'objectif cliquable
const Objective = ({ icon, title, description, link }) => (
  <Link to={link} className="bg-white bg-opacity-10 rounded-2xl p-6 flex flex-col items-center text-center max-w-xs hover:scale-105 transition-transform duration-300 shadow-lg hover:bg-opacity-20">
    <div className="mb-4 text-indigo-300">{icon}</div>
    <h3 className="text-xl font-semibold mb-2">{title}</h3>
    <p className="text-gray-300">{description}</p>
  </Link>
);

// Données simulées d'appels d'offres
const offers = [
  {
    title: "Fourniture de Matériel Informatique",
    description: "Le Ministère de l'Économie lance un appel pour l'acquisition de matériels informatiques.",
    deadline: "20 juillet 2025"
  },
  {
    title: "Travaux de Réhabilitation de Routes Rurales",
    description: "Projet de réhabilitation de 50 km de routes dans la région Vakinankaratra.",
    deadline: "25 juillet 2025"
  },
  {
    title: "Consultance pour Audit Financier",
    description: "Recrutement d’un cabinet pour un audit annuel des finances publiques.",
    deadline: "15 août 2025"
  }
];

export default Home;
