import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { 
  FaClipboardList, 
  FaCheckCircle, 
  FaChartBar,
  FaChartPie,
  FaArrowRight
} from 'react-icons/fa';
import { Bar, Pie } from 'react-chartjs-2';
import { Chart as ChartJS, registerables } from 'chart.js';

ChartJS.register(...registerables);

const DashboardEntreprise = () => {
  const [stats, setStats] = useState({
    totalOffres: 0,
    candidaturesApprouvees: 0,
    candidaturesEnAttente: 0,
    candidaturesRejetees: 0
  });
  const [loading, setLoading] = useState(true);
  const [chartData, setChartData] = useState(null);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const token = localStorage.getItem('token');
        const [offresRes, candidaturesRes] = await Promise.all([
          axios.get('http://localhost:3000/api/offres/count', {
            headers: { Authorization: `Bearer ${token}` }
          }),
          axios.get('http://localhost:3000/api/candidatures/stats', {
            headers: { Authorization: `Bearer ${token}` }
          })
        ]);

        setStats({
          totalOffres: offresRes.data.count,
          candidaturesApprouvees: candidaturesRes.data.approuvees,
          candidaturesEnAttente: candidaturesRes.data.enAttente,
          candidaturesRejetees: candidaturesRes.data.rejetees
        });

        prepareChartData({
          approuvees: candidaturesRes.data.approuvees,
          enAttente: candidaturesRes.data.enAttente,
          rejetees: candidaturesRes.data.rejetees
        });
      } catch (err) {
        console.error('Erreur chargement stats:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  const prepareChartData = (data) => {
    setChartData({
      bar: {
        labels: ['Approuvées', 'En attente', 'Rejetées'],
        datasets: [{
          label: 'Statut des Candidatures',
          data: [data.approuvees, data.enAttente, data.rejetees],
          backgroundColor: [
            'rgba(40, 167, 69, 0.7)',
            'rgba(255, 193, 7, 0.7)',
            'rgba(220, 53, 69, 0.7)'
          ],
          borderColor: [
            'rgba(40, 167, 69, 1)',
            'rgba(255, 193, 7, 1)',
            'rgba(220, 53, 69, 1)'
          ],
          borderWidth: 1
        }]
      },
      pie: {
        labels: ['Offres disponibles', 'Candidatures envoyées'],
        datasets: [{
          data: [stats.totalOffres, 
                data.approuvees + data.enAttente + data.rejetees],
          backgroundColor: [
            'rgba(13, 110, 253, 0.7)',
            'rgba(111, 66, 193, 0.7)'
          ],
          borderColor: [
            'rgba(13, 110, 253, 1)',
            'rgba(111, 66, 193, 1)'
          ],
          borderWidth: 1
        }]
      }
    });
  };

  return (
    <div className="min-h-screen p-6 bg-gradient-to-br from-gray-900 to-blue-900 text-white">
      <h1 className="text-3xl font-bold mb-8 text-center">Tableau de Bord Entreprise</h1>

      {loading ? (
        <div className="flex justify-center items-center h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-white"></div>
        </div>
      ) : (
        <>
          {/* Cartes de Statistiques */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
            <StatCard 
              icon={<FaClipboardList className="text-blue-400" size={24} />}
              title="Offres Disponibles"
              value={stats.totalOffres}
              link="/entreprise/offres"
              linkText="Voir les offres"
            />
            <StatCard 
              icon={<FaCheckCircle className="text-green-400" size={24} />}
              title="Candidatures Approuvées"
              value={stats.candidaturesApprouvees}
              link="/entreprise/candidatures?status=approuve"
              linkText="Voir détails"
            />
            <StatCard 
              icon={<FaChartBar className="text-yellow-400" size={24} />}
              title="Statut Candidatures"
              value={`${stats.candidaturesEnAttente} en attente`}
              link="/entreprise/candidatures"
              linkText="Voir le suivi"
            />
          </div>

          {/* Visualisations Graphiques */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
            <div className="bg-white bg-opacity-10 p-6 rounded-xl shadow-lg">
              <div className="flex items-center gap-2 mb-4">
                <FaChartBar className="text-blue-400" />
                <h3 className="text-xl font-semibold">Répartition des Candidatures</h3>
              </div>
              {chartData && (
                <Bar 
                  data={chartData.bar}
                  options={{
                    responsive: true,
                    plugins: {
                      legend: {
                        position: 'top',
                        labels: {
                          color: 'white'
                        }
                      }
                    },
                    scales: {
                      y: {
                        beginAtZero: true,
                        ticks: {
                          color: 'white'
                        },
                        grid: {
                          color: 'rgba(255, 255, 255, 0.1)'
                        }
                      },
                      x: {
                        ticks: {
                          color: 'white'
                        },
                        grid: {
                          color: 'rgba(255, 255, 255, 0.1)'
                        }
                      }
                    }
                  }}
                />
              )}
            </div>

            <div className="bg-white bg-opacity-10 p-6 rounded-xl shadow-lg">
              <div className="flex items-center gap-2 mb-4">
                <FaChartPie className="text-purple-400" />
                <h3 className="text-xl font-semibold">Ratio Offres/Candidatures</h3>
              </div>
              {chartData && (
                <Pie 
                  data={chartData.pie}
                  options={{
                    responsive: true,
                    plugins: {
                      legend: {
                        position: 'top',
                        labels: {
                          color: 'white'
                        }
                      }
                    }
                  }}
                />
              )}
            </div>
          </div>

          {/* Actions Rapides */}
          <div className="bg-white bg-opacity-10 p-6 rounded-xl shadow-lg">
            <h3 className="text-xl font-semibold mb-4">Actions Rapides</h3>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <ActionCard 
                title="Postuler à une offre"
                description="Trouvez de nouvelles opportunités"
                link="/entreprise/postuler"
                color="bg-blue-600 hover:bg-blue-700"
              />
              <ActionCard 
                title="Suivi candidatures"
                description="Consultez l'état de vos candidatures"
                link="/entreprise/candidatures"
                color="bg-purple-600 hover:bg-purple-700"
              />
              <ActionCard 
                title="Offres approuvées"
                description="Vos candidatures acceptées"
                link="/entreprise/candidatures?status=approuve"
                color="bg-green-600 hover:bg-green-700"
              />
            </div>
          </div>
        </>
      )}
    </div>
  );
};

const StatCard = ({ icon, title, value, link, linkText }) => (
  <div className="bg-white bg-opacity-10 p-6 rounded-xl shadow-lg hover:bg-opacity-20 transition-all">
    <div className="flex items-center gap-3 mb-4">
      {icon}
      <h3 className="text-lg font-semibold">{title}</h3>
    </div>
    <p className="text-3xl font-bold mb-4">{value}</p>
    <Link to={link} className="inline-flex items-center text-blue-300 hover:text-blue-200 text-sm">
      {linkText} <FaArrowRight className="ml-1" />
    </Link>
  </div>
);

const ActionCard = ({ title, description, link, color }) => (
  <Link 
    to={link}
    className={`${color} p-4 rounded-lg shadow-md transition-all transform hover:scale-105`}
  >
    <h4 className="font-bold text-lg mb-2">{title}</h4>
    <p className="text-sm opacity-90">{description}</p>
  </Link>
);

export default DashboardEntreprise;
