import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  FaPaperPlane, FaSpinner, FaCheck, FaTimes,
  FaFilePdf, FaFileWord, FaFileImage
} from 'react-icons/fa';
import { useParams, useNavigate } from 'react-router-dom';

const Postuler = () => {
  const { offerd } = useParams();
  const navigate = useNavigate();
  const [offer, setOffer] = useState(null);
  const [file, setFile] = useState(null);
  const [filePreview, setFilePreview] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [message, setMessage] = useState('');
  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchOffer = async () => {
      try {
        const res = await axios.get(`/api/entreprise/offres/${offerId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setOffer(res.data);
      } catch (err) {
        setMessage("Impossible de charger l'offre.");
        console.error(err);
      }
    };

    fetchOffer();
  }, [offerId, token]);

  const handleFileChange = (e) => {
    const selected = e.target.files[0];
    if (!selected) return;

    if (selected.size > 5 * 1024 * 1024) {
      setMessage("Le fichier dépasse 5MB.");
      return;
    }

    setFile(selected);
    const reader = new FileReader();
    reader.onloadend = () => {
      setFilePreview({
        name: selected.name,
        type: selected.type,
        size: selected.size,
        url: reader.result
      });
    };
    reader.readAsDataURL(selected);
  };

  const getFileIcon = (type) => {
    if (type.includes("pdf")) return <FaFilePdf className="text-red-500 text-2xl" />;
    if (type.includes("word") || type.includes("msword")) return <FaFileWord className="text-blue-500 text-2xl" />;
    if (type.includes("image")) return <FaFileImage className="text-green-500 text-2xl" />;
    return <FaFilePdf className="text-gray-500 text-2xl" />;
  };

  const formatFileSize = (bytes) => {
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return `${(bytes / Math.pow(k, i)).toFixed(1)} ${sizes[i]}`;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      setMessage("Veuillez ajouter un fichier.");
      return;
    }

    setIsLoading(true);
    const formData = new FormData();
    formData.append('offerId', offerId);
    formData.append('file', file);
    formData.append('status', 'pending');
    formData.append('viewedByMinistere', false);
    formData.append('viewedByAdmin', false);

    try {
      await axios.post('/api/candidatures', formData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });
      setIsSubmitted(true);
      setMessage("Candidature soumise avec succès.");
      setTimeout(() => navigate('/dashboard/entreprise/mes-candidatures'), 2000);
    } catch (err) {
      setMessage(err.response?.data?.message || "Erreur lors de la soumission.");
      console.error(err);
    } finally {
      setIsLoading(false);
    }
  };

  if (!offer) {
    return <div className="p-8 text-center text-gray-600">Chargement de l'offre...</div>;
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8 px-4">
      <div className="max-w-2xl mx-auto bg-white rounded-xl shadow-md p-8">
        <button onClick={() => navigate(-1)} className="text-blue-600 hover:text-blue-800 mb-4">
          &larr; Retour
        </button>

        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-bold text-gray-800">
            Postuler à : {offer.title}
          </h2>
          {isSubmitted && (
            <span className="inline-flex items-center px-3 py-1 rounded-full bg-green-100 text-green-700 text-sm">
              <FaCheck className="mr-1" /> Envoyée
            </span>
          )}
        </div>

        {message && (
          <div className={`mb-4 p-3 rounded-md ${
            message.includes("succès") ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'
          }`}>
            {message}
          </div>
        )}

        <div className="mb-6 bg-blue-50 p-4 rounded-lg">
          <h3 className="font-semibold text-blue-800">Détails de l'offre</h3>
          <p className="text-sm mt-1 text-gray-700">{offer.description}</p>
          <p className="text-sm mt-2"><strong>Entreprise :</strong> {offer.company}</p>
          <p className="text-sm"><strong>Date limite :</strong> {new Date(offer.deadline).toLocaleDateString()}</p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Joindre votre fichier <span className="text-red-500">*</span>
            </label>

            {filePreview ? (
              <div className="flex items-center justify-between border rounded p-3">
                <div className="flex items-center gap-3">
                  {getFileIcon(filePreview.type)}
                  <div>
                    <p className="text-sm font-medium text-gray-900 truncate max-w-xs">{filePreview.name}</p>
                    <p className="text-xs text-gray-500">{formatFileSize(filePreview.size)}</p>
                  </div>
                </div>
                <button type="button" onClick={() => {
                  setFile(null);
                  setFilePreview(null);
                }} className="text-red-500 hover:text-red-700">
                  <FaTimes />
                </button>
              </div>
            ) : (
              <label htmlFor="file-upload" className="flex flex-col items-center justify-center h-32 w-full border-2 border-dashed border-gray-300 rounded-lg cursor-pointer hover:border-blue-400">
                <div className="flex flex-col items-center justify-center pt-4">
                  <FaPaperPlane className="w-8 h-8 text-gray-400 mb-2" />
                  <p className="text-sm text-gray-500">
                    <span className="font-semibold">Cliquez ou déposez</span> un fichier ici
                  </p>
                  <p className="text-xs text-gray-400">PDF, DOCX, JPG (max. 5MB)</p>
                </div>
                <input
                  id="file-upload"
                  type="file"
                  accept=".pdf,.doc,.docx,.jpg,.jpeg,.png"
                  className="hidden"
                  onChange={handleFileChange}
                />
              </label>
            )}
          </div>

          <button
            type="submit"
            disabled={isLoading || isSubmitted}
            className={`w-full flex justify-center items-center gap-2 py-3 px-4 rounded-md text-white text-sm font-medium ${
              isLoading ? 'bg-blue-400 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-700'
            }`}
          >
            {isLoading ? (
              <>
                <FaSpinner className="animate-spin" /> Envoi en cours...
              </>
            ) : isSubmitted ? (
              <>
                <FaCheck /> Candidature envoyée
              </>
            ) : (
              <>
                <FaPaperPlane /> Soumettre ma candidature
              </>
            )}
          </button>
        </form>

        <div className="mt-6 border-t pt-4 text-sm text-gray-600">
          <h4 className="font-medium text-gray-800 mb-1">Étapes suivantes</h4>
          <ul className="space-y-1 list-disc pl-5">
            <li>Validation par le ministère</li>
            <li>Révision par l'administrateur</li>
            <li>Notification par email une fois le processus terminé</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Postuler;
