import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Bar, Pie, Line } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
import { FaHome, FaUsers, FaBuilding, FaLandmark, FaFileAlt } from 'react-icons/fa';

Chart.register(...registerables);

const DashboardAdmin = () => {
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/');
          return;
        }

        const response = await fetch('http://localhost:3000/api/dashboard/admin', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        if (!response.ok) {
          throw new Error('Accès non autorisé');
        }

        const data = await response.json();
        setDashboardData(data);
      } catch (err) {
        setError(err.message);
        navigate('/');
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, [navigate]);

  const stats = [
    { name: 'Utilisateurs', value: dashboardData?.stats.users, change: '+12%', icon: <FaUsers className="text-2xl" /> },
    { name: 'Entreprises', value: dashboardData?.stats.entreprises, change: '+5%', icon: <FaBuilding className="text-2xl" /> },
    { name: 'Ministères', value: dashboardData?.stats.ministeres, change: '+2%', icon: <FaLandmark className="text-2xl" /> },
    { name: 'Offres', value: dashboardData?.stats.offers, change: '+23%', icon: <FaFileAlt className="text-2xl" /> },
  ];

  const userRoleData = {
    labels: ['Entreprises', 'Ministères', 'Admins'],
    datasets: [
      {
        data: [
          dashboardData?.stats.entreprises,
          dashboardData?.stats.ministeres,
          1
        ],
        backgroundColor: [
          '#3B82F6',
          '#10B981',
          '#6366F1'
        ],
        borderWidth: 0,
      },
    ],
  };

  const offersTimelineData = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
    datasets: [
      {
        label: 'Offres créées',
        data: [12, 19, 8, 15, 22, 18],
        fill: false,
        backgroundColor: '#3B82F6',
        borderColor: '#3B82F6',
        tension: 0.4,
      },
    ],
  };

  const recentUsers = dashboardData?.users.slice(0, 5) || [];

  if (loading) return (
    <div className="flex items-center justify-center h-screen">
      <div className="animate-spin rounded-full h-32 w-32 border-t-2 border-b-2 border-indigo-500"></div>
    </div>
  );

  if (error) return (
    <div className="flex items-center justify-center h-screen">
      <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
        {error}
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-100 p-4 md:p-8">
      <div className="flex justify-between items-center mb-8">
        <div>
          <h1 className="text-2xl md:text-3xl font-bold text-gray-800">
            {dashboardData?.message}
          </h1>
          <p className="text-gray-600">Bienvenue sur votre tableau de bord administrateur</p>
        </div>
        <button
          onClick={() => navigate('/')}
          className="flex items-center gap-2 bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2 rounded-lg transition-colors"
        >
          <FaHome /> Accueil
        </button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        {stats.map((stat, index) => (
          <div
            key={stat.name}
            className="bg-white p-6 rounded-xl shadow-md hover:shadow-lg transition-shadow"
          >
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-500">{stat.name}</p>
                <p className="text-2xl font-semibold text-gray-800">{stat.value}</p>
                <p className="text-sm text-green-500">{stat.change}</p>
              </div>
              <span className="text-indigo-600">{stat.icon}</span>
            </div>
          </div>
        ))}
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-8">
        <div className="bg-white p-6 rounded-xl shadow-md lg:col-span-2">
          <h2 className="text-lg font-semibold mb-4">Activité des offres</h2>
          <Line 
            data={offersTimelineData}
            options={{
              responsive: true,
              plugins: {
                legend: {
                  position: 'top',
                },
              },
            }}
          />
        </div>

        <div className="bg-white p-6 rounded-xl shadow-md">
          <h2 className="text-lg font-semibold mb-4">Répartition des utilisateurs</h2>
          <div className="h-64">
            <Pie 
              data={userRoleData}
              options={{
                responsive: true,
                maintainAspectRatio: false,
              }}
            />
          </div>
        </div>
      </div>

      <div className="bg-white p-6 rounded-xl shadow-md mb-8">
        <h2 className="text-lg font-semibold mb-4">Derniers utilisateurs inscrits</h2>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Nom</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Email</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Rôle</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {recentUsers.map((user) => (
                <tr key={user.username}>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{user.username}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{user.email}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full 
                      ${user.role === 'admin' ? 'bg-purple-100 text-purple-800' : 
                        user.role === 'ministere' ? 'bg-green-100 text-green-800' : 
                        'bg-blue-100 text-blue-800'}`}>
                      {user.role}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      <div className="bg-white p-6 rounded-xl shadow-md">
        <h2 className="text-lg font-semibold mb-4">Dernières offres</h2>
        <div className="space-y-4">
          {dashboardData?.offers.map((offer) => (
            <div key={offer.id} className="border-l-4 border-indigo-500 pl-4 py-2">
              <h3 className="font-medium text-gray-800">{offer.title}</h3>
              <p className="text-sm text-gray-600">{offer.description.substring(0, 100)}...</p>
              <p className="text-xs text-gray-500 mt-1">Créé le: {new Date(offer.createdAt).toLocaleDateString()}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default DashboardAdmin;
