// src/Pages/Register.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    email: '',
    companyName: '',
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    const res = await fetch('http://localhost:3000/api/auth/register', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData)
    });

    const data = await res.json();

    if (!res.ok) {
      setError(data.error || 'Inscription échouée');
    } else {
      setSuccess('Inscription réussie. Redirection...');
      setTimeout(() => navigate('/'), 2000);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <form
        onSubmit={handleRegister}
        className="bg-white p-6 rounded-2xl shadow-lg w-full max-w-md"
      >
        <h2 className="text-2xl font-bold mb-6 text-center text-blue-700">Inscription</h2>

        {error && <p className="text-red-600 mb-2">{error}</p>}
        {success && <p className="text-green-600 mb-2">{success}</p>}

        <input name="username" onChange={handleChange} required placeholder="Nom d'utilisateur"
          className="w-full p-2 mb-3 border rounded" />
        <input name="email" type="email" onChange={handleChange} required placeholder="Email"
          className="w-full p-2 mb-3 border rounded" />
        <input name="companyName" onChange={handleChange} placeholder="Nom de l'entreprise"
          className="w-full p-2 mb-3 border rounded" />
        <input name="password" type="password" onChange={handleChange} required placeholder="Mot de passe"
          className="w-full p-2 mb-5 border rounded" />

        <button type="submit" className="w-full bg-blue-600 text-white py-2 rounded">S'inscrire</button>
      </form>
    </div>
  );
};

export default Register;
