import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import {
  FaSignOutAlt,
  FaSearch,
  FaPaperPlane,
} from 'react-icons/fa';

const DashboardEntreprise = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [offers, setOffers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  // üîÅ Chargement des offres disponibles
  useEffect(() => {
    axios.get('http://localhost:5000/api/offres/validees')
      .then(res => setOffers(res.data))
      .catch(err => console.error(err));
  }, []);

  // üîç Filtrage des offres par recherche
  const filteredOffers = offers.filter(offer =>
    offer.titre.toLowerCase().includes(searchTerm.toLowerCase()) ||
    offer.description.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // üì¶ Gestion d√©connexion
  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  // üîΩ Scroll vers la section ancr√©e
  useEffect(() => {
    if (location.hash === '#offres-section') {
      const element = document.getElementById('offres-section');
      if (element) {
        setTimeout(() => {
          element.scrollIntoView({ behavior: 'smooth' });
        }, 100);
      }
    }
  }, [location]);

  return (
    <div className="min-h-screen bg-gradient-to-r from-green-100 to-blue-200 p-8">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-3xl font-bold text-blue-800">Dashboard Entreprise</h2>
        <button
          onClick={handleLogout}
          className="flex items-center bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600 transition"
        >
          <FaSignOutAlt className="mr-2" />
          D√©connexion
        </button>
      </div>

      {/* üîç Barre de recherche */}
      <div className="mb-6 flex items-center">
        <FaSearch className="text-gray-500 mr-2" />
        <input
          type="text"
          placeholder="Rechercher une offre..."
          className="p-2 rounded border border-gray-300 flex-1"
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
      </div>

      {/* üéØ SECTION OFFRES AVEC ID POUR L‚ÄôANCRE */}
      <div id="offres-section" className="bg-white bg-opacity-80 p-6 rounded-xl shadow mb-8">
        <h3 className="text-xl font-semibold mb-4 text-blue-800">Derni√®res Offres Disponibles</h3>
        {filteredOffers.length === 0 ? (
          <p className="text-gray-600">Aucune offre disponible.</p>
        ) : (
          <ul className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredOffers.map((offer, index) => (
              <li key={index} className="bg-white p-4 rounded shadow hover:shadow-lg transition">
                <h4 className="text-lg font-bold text-indigo-700">{offer.titre}</h4>
                <p className="text-gray-700 mt-2">{offer.description}</p>
                <p className="text-sm text-gray-500 mt-1">Domaine : {offer.domaine}</p>
                <p className="text-sm text-gray-500">Date limite : {offer.date_limite}</p>
                <button className="mt-3 bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded flex items-center">
                  <FaPaperPlane className="mr-2" />
                  Postuler
                </button>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default DashboardEntreprise;
