import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  FaClipboardList,
  FaCheckCircle,
  FaChartBar,
  FaSpinner,
} from 'react-icons/fa';

const DashboardEntreprise = () => {
  const navigate = useNavigate();
  const [offers, setOffers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchOffers = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5000/api/entreprise/offres/publiees', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setOffers(res.data);
        setLoading(false);
      } catch (error) {
        console.error('Erreur lors du chargement des offres', error);
        setLoading(false);
      }
    };

    fetchOffers();
  }, []);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <FaSpinner className="animate-spin text-4xl text-blue-600" />
        <p className="ml-2">Chargement des offres...</p>
      </div>
    );
  }

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold text-gray-800 mb-4 flex items-center gap-2">
        <FaChartBar /> Tableau de bord - Entreprise
      </h2>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        <div className="bg-green-100 p-4 rounded-lg shadow flex items-center">
          <FaCheckCircle className="text-green-600 text-3xl mr-4" />
          <div>
            <p className="text-lg font-semibold">{offers.length}</p>
            <p className="text-sm">Offres valides disponibles</p>
          </div>
        </div>
      </div>

      <h3 className="text-xl font-semibold mb-2 flex items-center gap-2">
        <FaClipboardList /> Liste des Offres Valid√©es
      </h3>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {offers.map((offer) => (
          <div key={offer._id} className="bg-white p-4 rounded-lg shadow">
            <h4 className="text-lg font-bold text-blue-700">{offer.titre}</h4>
            <p className="text-sm text-gray-600 mb-2">{offer.description}</p>
            <p className="text-sm text-gray-500">
              Domaine : <span className="font-medium">{offer.domaine}</span>
            </p>
            <p className="text-sm text-gray-500">
              Date limite : <span className="font-medium">{offer.date_limite}</span>
            </p>

            {offer._id && (
              <button
                onClick={() => navigate(`/entreprise/postuler/${offer._id}`)}
                className="mt-3 bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded text-sm"
              >
                Postuler
              </button>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default DashboardEntreprise;
