import React, { createContext, useContext, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);

  // Au montage, on vérifie si un token est déjà présent dans localStorage
  useEffect(() => {
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');
    if (token && userData) {
      setUser(JSON.parse(userData));
    }
  }, []);

  const login = async ({ token, role, ...restUser }) => {
    // Stocker token et user dans localStorage
    localStorage.setItem('token', token);
    localStorage.setItem('user', JSON.stringify({ role, ...restUser }));
    setUser({ role, ...restUser });

    // Redirection selon rôle
    if (role === 'ministere' || role === 'admin') {
      navigate('/ministere/home', { replace: true });
    } else if (role === 'entreprise') {
      navigate('/entreprise/home', { replace: true });
    } else {
      navigate('/', { replace: true });
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    navigate('/login', { replace: true });
  };

  return (
    <AuthContext.Provider value={{ user, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
