// DashboardEntreprise.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaSignOutAlt, FaSpinner, FaCheckCircle } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';

const DashboardEntreprise = () => {
  const navigate = useNavigate();
  const [offers, setOffers] = useState([]);
  const [stats, setStats] = useState({ approuvees: 0, enAttente: 0, rejetees: 0 });
  const [loading, setLoading] = useState(true);
  const token = localStorage.getItem('token');

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    setLoading(true);
    try {
      const res = await axios.get('http://localhost:3000/api/dashboard/entreprise', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setStats(res.data.stats);
      setOffers(res.data.offers);
    } catch (err) {
      console.error('Erreur lors du chargement des données entreprise :', err);
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  return (
    <div className="min-h-screen p-8 bg-gradient-to-r from-green-100 via-green-200 to-green-300 text-green-900">
      <header className="flex justify-between items-center mb-8">
        <h1 className="text-2xl md:text-4xl font-bold">Espace Entreprise</h1>
        <button
          onClick={handleLogout}
          className="flex items-center gap-2 bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg"
        >
          <FaSignOutAlt /> Déconnexion
        </button>
      </header>

      {loading ? (
        <div className="flex justify-center items-center h-40">
          <FaSpinner className="animate-spin text-3xl text-green-600" />
        </div>
      ) : (
        <>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-10">
            <StatBox title="Candidatures approuvées" value={stats.approuvees} />
            <StatBox title="Candidatures en attente" value={stats.enAttente} />
            <StatBox title="Candidatures rejetées" value={stats.rejetees} />
          </div>

          <section className="bg-white bg-opacity-80 p-6 rounded-xl shadow">
            <h2 className="text-2xl font-semibold mb-4">Offres disponibles</h2>
            {offers.length === 0 ? (
              <p className="text-green-700 italic">Aucune offre validée disponible pour l'instant.</p>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {offers.map(offer => (
                  <div key={offer.id} className="p-4 bg-white rounded-lg shadow border-l-4 border-green-500">
                    <h3 className="text-lg font-bold text-green-900">{offer.title}</h3>
                    <p className="text-green-700 text-sm mb-2">{offer.description}</p>
                    <p className="text-green-600 text-xs">Date limite : {new Date(offer.deadline).toLocaleDateString()}</p>
                  </div>
                ))}
              </div>
            )}
          </section>
        </>
      )}
    </div>
  );
};

const StatBox = ({ title, value }) => (
  <div className="bg-white bg-opacity-80 p-6 rounded-xl shadow-md text-center">
    <FaCheckCircle className="text-green-500 text-2xl mb-2 mx-auto" />
    <h3 className="text-lg font-semibold">{title}</h3>
    <p className="text-2xl font-bold">{value}</p>
  </div>
);

export default DashboardEntreprise;
