// DashboardAdmin.jsx - Gérer les utilisateurs et statistiques + bouton création
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  FaHome, FaUsers, FaBuilding, FaLandmark,
  FaFileAlt, FaSignOutAlt, FaChartPie, FaPlus
} from 'react-icons/fa';
import { Line, Pie } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';

Chart.register(...registerables);

const DashboardAdmin = () => {
  const [dashboard, setDashboard] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const fetchDashboard = () => {
    const token = localStorage.getItem('token');
    if (!token) return navigate('/');

    setLoading(true);
    fetch('http://localhost:3000/api/dashboard/admin', {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
      .then(res => {
        if (!res.ok) throw new Error('Erreur de chargement');
        return res.json();
      })
      .then(data => {
        setDashboard(data);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchDashboard();
  }, [navigate]);

  const handleDeleteUser = (username) => {
    if (!window.confirm(`Supprimer ${username} ?`)) return;
    const token = localStorage.getItem('token');
    fetch(`http://localhost:3000/api/users/${username}`, {
      method: 'DELETE',
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
      .then(res => res.json())
      .then(data => {
        alert(data.message || "Utilisateur supprimé");
        fetchDashboard();
      })
      .catch(() => alert("Erreur lors de la suppression"));
  };

  if (loading) return <div className="flex items-center justify-center h-screen"><div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-indigo-500"></div></div>;
  if (error) return <div className="flex items-center justify-center h-screen"><div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">{error}</div></div>;
  if (!dashboard) return null;

  const lineChartData = {
    labels: ['Jan', 'Fév', 'Mar', 'Avr', 'Mai', 'Juin'],
    datasets: [{
      label: 'Offres créées',
      data: [2, 4, 7, 3, 8, 6],
      borderColor: '#6366F1',
      backgroundColor: '#E0E7FF',
      borderWidth: 2,
      fill: true
    }]
  };

  const pieChartData = {
    labels: ['Entreprises', 'Ministères', 'Admins'],
    datasets: [{
      data: [dashboard.stats.entreprises, dashboard.stats.ministeres, 1],
      backgroundColor: ['#3B82F6', '#10B981', '#8B5CF6'],
      borderWidth: 0
    }]
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: { position: 'bottom' }
    },
    animation: { duration: 0 }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  return (
    <div className="flex min-h-screen bg-gray-100">
      <div className="w-64 bg-gray-800 text-white py-8 px-4 fixed h-full">
        <h2 className="text-2xl font-bold text-center mb-8">Admin Panel</h2>
        <nav className="space-y-2">
          <SidebarButton icon={<FaHome />} label="Accueil" onClick={() => navigate('/')} />
          <SidebarButton icon={<FaChartPie />} label="Statistiques" onClick={() => window.scrollTo(0, 0)} />
          <SidebarButton icon={<FaUsers />} label="Utilisateurs" onClick={() => document.getElementById('users-section')?.scrollIntoView({ behavior: 'smooth' })} />
          <SidebarButton icon={<FaFileAlt />} label="Offres" onClick={() => document.getElementById('offers-section')?.scrollIntoView({ behavior: 'smooth' })} />
          <SidebarButton icon={<FaPlus />} label="Créer Offre" onClick={() => navigate('/offre/Creer')} />
          <div className="pt-4 mt-4 border-t border-gray-700">
            <SidebarButton icon={<FaSignOutAlt />} label="Déconnexion" onClick={handleLogout} isLogout />
          </div>
        </nav>
      </div>

      <div className="flex-1 ml-64 p-6">
        <h1 className="text-3xl font-bold text-gray-800 mb-8">Tableau de Bord Administrateur</h1>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <StatCard label="Utilisateurs" value={dashboard.stats.users} icon={<FaUsers />} color="indigo" />
          <StatCard label="Entreprises" value={dashboard.stats.entreprises} icon={<FaBuilding />} color="green" />
          <StatCard label="Ministères" value={dashboard.stats.ministeres} icon={<FaLandmark />} color="yellow" />
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
          <div className="bg-white p-6 rounded-lg shadow">
            <h2 className="text-xl font-semibold mb-4">Activité des offres</h2>
            <div className="h-80">
              <Line data={lineChartData} options={chartOptions} />
            </div>
          </div>
          <div className="bg-white p-6 rounded-lg shadow">
            <h2 className="text-xl font-semibold mb-4">Répartition des utilisateurs</h2>
            <div className="h-80">
              <Pie data={pieChartData} options={chartOptions} />
            </div>
          </div>
        </div>

        <div id="users-section" className="bg-white rounded-lg shadow mb-8">
          <div className="p-6 border-b border-gray-200">
            <h2 className="text-2xl font-bold">Liste des Utilisateurs</h2>
          </div>
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Nom</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Email</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Rôle</th>
                  <th className="px-6 py-3">Action</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {dashboard.users.map((user, index) => (
                  <tr key={index}>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{user.username}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{user.email}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 capitalize">{user.role}</td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <button onClick={() => handleDeleteUser(user.username)} className="bg-red-600 text-white px-3 py-1 rounded hover:bg-red-700">Supprimer</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        <div id="offers-section" className="bg-white rounded-lg shadow">
          <div className="p-6 border-b border-gray-200">
            <h2 className="text-2xl font-bold">Dernières Offres</h2>
          </div>
          <div className="divide-y divide-gray-200">
            {dashboard.offers.map((offer, index) => (
              <div key={index} className="p-6 hover:bg-gray-50 transition-colors">
                <h3 className="text-lg font-semibold text-indigo-600">{offer.title}</h3>
                <p className="text-sm text-gray-500 mt-1">Créé par {offer.createdBy} • {new Date(offer.createdAt).toLocaleDateString()}</p>
                <p className="text-gray-700 mt-2">{offer.description.substring(0, 120)}...</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

const SidebarButton = ({ icon, label, onClick, isLogout = false }) => (
  <button onClick={onClick} className={`flex items-center w-full px-4 py-3 rounded-md transition-colors ${isLogout ? 'text-red-400 hover:bg-red-900/10' : 'text-gray-300 hover:bg-gray-700 hover:text-white'}`}>
    <span className="mr-3">{icon}</span>
    <span>{label}</span>
  </button>
);

const StatCard = ({ label, value, icon, color }) => {
  const colorClasses = {
    indigo: 'bg-indigo-100 text-indigo-600',
    green: 'bg-green-100 text-green-600',
    yellow: 'bg-yellow-100 text-yellow-600',
    red: 'bg-red-100 text-red-600'
  };

  return (
    <div className={`${colorClasses[color]} p-6 rounded-lg shadow flex justify-between items-center`}>
      <div>
        <p className="text-sm font-medium">{label}</p>
        <p className="text-2xl font-bold">{value}</p>
      </div>
      <div className="text-3xl opacity-80">{icon}</div>
    </div>
  );
};

export default DashboardAdmin;
