// DashboardMinistere.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  FaCheckCircle,
  FaTimesCircle,
  FaChartBar,
  FaSpinner,
  FaSignOutAlt,
  FaSearch,
  FaFilter,
  FaTrash
} from 'react-icons/fa';
import DashboardChart from './DashboardChart';

const DashboardMinistere = ({ toggleDarkMode, darkMode }) => {
  const navigate = useNavigate();
  const [recentOffers, setRecentOffers] = useState([]);
  const [offresEnAttente, setOffresEnAttente] = useState([]);
  const [offresPubliees, setOffresPubliees] = useState([]);
  const [stats, setStats] = useState({ total: 0, actifs: 0, rejetees: 0 });
  const [loading, setLoading] = useState({
    dashboard: false,
    pending: false,
    published: false,
    actions: {}
  });
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({ statut: 'tous' });
  const token = localStorage.getItem('token');

  useEffect(() => {
    fetchDashboard();
    fetchOffresEnAttente();
    fetchOffresPubliees();
  }, []);

  const fetchDashboard = async () => {
    setLoading(prev => ({ ...prev, dashboard: true }));
    try {
      const res = await axios.get('http://localhost:3000/api/dashboard/ministere', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setRecentOffers(res.data.recentOffers || []);
      setStats(res.data.stats || { total: 0, actifs: 0, rejetees: 0 });
    } catch (err) {
      console.error('Erreur lors du chargement des données', err);
    } finally {
      setLoading(prev => ({ ...prev, dashboard: false }));
    }
  };

  const fetchOffresEnAttente = async () => {
    setLoading(prev => ({ ...prev, pending: true }));
    try {
      const res = await axios.get('http://localhost:3000/api/offres/en-attente', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOffresEnAttente(res.data || []);
    } catch (err) {
      console.error('Erreur lors du chargement des offres en attente', err);
    } finally {
      setLoading(prev => ({ ...prev, pending: false }));
    }
  };

  const fetchOffresPubliees = async () => {
    setLoading(prev => ({ ...prev, published: true }));
    try {
      const res = await axios.get('http://localhost:3000/api/offres/publiees', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOffresPubliees(res.data || []);
    } catch (err) {
      console.error('Erreur lors du chargement des offres publiées', err);
    } finally {
      setLoading(prev => ({ ...prev, published: false }));
    }
  };

  const validerOffre = async (id) => {
    setLoading(prev => ({ ...prev, actions: { ...prev.actions, [id]: 'validation' } }));
    try {
      await axios.put(`http://localhost:3000/api/ministere/offres/${id}/valider`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      await Promise.all([fetchDashboard(), fetchOffresEnAttente(), fetchOffresPubliees()]);
    } catch (err) {
      console.error('Erreur lors de la validation', err);
    } finally {
      setLoading(prev => ({ ...prev, actions: { ...prev.actions, [id]: false } }));
    }
  };

  const rejeterOffre = async (id) => {
    setLoading(prev => ({ ...prev, actions: { ...prev.actions, [id]: 'rejet' } }));
    try {
      await axios.post(`http://localhost:3000/api/ministere/rejeter/${id}`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      await Promise.all([fetchDashboard(), fetchOffresEnAttente(), fetchOffresPubliees()]);
    } catch (err) {
      console.error('Erreur lors du rejet', err);
    } finally {
      setLoading(prev => ({ ...prev, actions: { ...prev.actions, [id]: false } }));
    }
  };

  const supprimerOffre = async (id) => {
    try {
      await axios.delete(`http://localhost:3000/api/offres/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      await fetchDashboard();
    } catch (err) {
      console.error('Erreur lors de la suppression', err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  const filterByStatut = (offer) => {
    if (filters.statut === 'tous') return true;
    if (filters.statut === 'actives') return offer.status === 'valide';
    if (filters.statut === 'rejetees') return offer.status === 'rejected' || offer.status === 'rejetée';
    return true;
  };

  const filteredRecentOffers = recentOffers.filter(offer => (
    (offer.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    offer.description.toLowerCase().includes(searchTerm.toLowerCase())) &&
    filterByStatut(offer)
  ));

  const filteredPendingOffers = offresEnAttente.filter(offer => (
    offer.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    offer.description.toLowerCase().includes(searchTerm.toLowerCase())
  ));

  const filteredPublishedOffers = offresPubliees.filter(offer => (
    (offer.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    offer.description.toLowerCase().includes(searchTerm.toLowerCase())) &&
    filterByStatut(offer)
  ));

  return (
    <div className="min-h-screen p-4 md:p-10 bg-gradient-to-r from-blue-100 via-blue-200 to-blue-300 text-blue-900 dark:bg-gray-900 dark:text-white">
      <header className="flex justify-between items-center mb-8">
        <h1 className="text-2xl md:text-4xl font-bold">Espace Ministère</h1>
        <div className="flex gap-4">
          <button
            onClick={toggleDarkMode}
            className="px-4 py-2 bg-gray-800 text-white dark:bg-gray-200 dark:text-black rounded"
          >
            {darkMode ? 'Mode Clair' : 'Mode Sombre'}
          </button>
          <button
            onClick={handleLogout}
            className="flex items-center gap-2 bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg transition-colors"
          >
            <FaSignOutAlt /> Déconnexion
          </button>
        </div>
      </header>

      <DashboardChart stats={stats} />
      {/* Statistiques */}
      {loading.dashboard ? (
        <div className="flex justify-center items-center h-40">
          <FaSpinner className="animate-spin text-3xl text-blue-400" />
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-10">
          <StatBox icon={<FaChartBar size={30} />} title="Offres totales" value={stats.total} />
          <StatBox icon={<FaCheckCircle size={30} />} title="Offres validées" value={stats.actifs} />
          <StatBox icon={<FaTimesCircle size={30} />} title="Offres rejetées" value={stats.rejetees} />
        </div>
      )}

      {/* Listes des offres en attente */}
      <section className="mb-10 bg-white dark:bg-gray-800 p-6 rounded-xl shadow">
        <h2 className="text-2xl font-semibold mb-4">Offres en Attente</h2>
        {loading.pending ? (
          <div className="flex justify-center items-center h-40">
            <FaSpinner className="animate-spin text-3xl text-blue-400" />
          </div>
        ) : (
          <div className="space-y-4">
            {filteredPendingOffers.map(offer => (
              <OfferCard
                key={offer.id}
                offer={offer}
                onValidate={() => validerOffre(offer.id)}
                onReject={() => rejeterOffre(offer.id)}
                onDelete={() => supprimerOffre(offer.id)}
                loading={loading.actions[offer.id]}
              />
            ))}
          </div>
        )}
      </section>

      {/* Dernières offres */}
      <section className="mb-10 bg-white dark:bg-gray-800 p-6 rounded-xl shadow">
        <h2 className="text-2xl font-semibold mb-4">Dernières Offres</h2>
        {filteredRecentOffers.length === 0 ? (
          <p className="italic">Aucune offre récente</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {filteredRecentOffers.map(offer => (
              <OfferCard
                key={offer.id}
                offer={offer}
                onValidate={() => validerOffre(offer.id)}
                onReject={() => rejeterOffre(offer.id)}
                onDelete={() => supprimerOffre(offer.id)}
                loading={loading.actions[offer.id]}
              />
            ))}
          </div>
        )}
      </section>

      {/* Offres publiées */}
      <section className="mb-10 bg-white dark:bg-gray-800 p-6 rounded-xl shadow">
        <h2 className="text-2xl font-semibold mb-4">Offres Publiées</h2>
        {filteredPublishedOffers.length === 0 ? (
          <p className="italic">Aucune offre publiée</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {filteredPublishedOffers.map(offer => (
              <OfferCard
                key={offer.id}
                offer={offer}
                isPublished={true}
              />
            ))}
          </div>
        )}
      </section>
    </div>
  );
};

export default DashboardMinistere;
