// CreerOffre.jsx - Page pour créer une nouvelle offre
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const CreerOffre = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [deadline, setDeadline] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) return alert("Non autorisé");

    try {
      const res = await fetch('http://localhost:3000/api/admin/offres', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({ title, description, deadline })
      });
      const data = await res.json();
      if (res.ok) {
        setMessage('Offre créée avec succès. En attente de validation.');
        setTimeout(() => navigate('/admin/dashboard'), 2000);
      } else {
        setMessage(data.error || 'Erreur lors de la création');
      }
    } catch (err) {
      setMessage('Erreur réseau');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="bg-white shadow-md rounded-lg p-8 max-w-lg w-full">
        <h2 className="text-2xl font-bold mb-6 text-gray-800 text-center">Créer une Nouvelle Offre</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700">Titre</label>
            <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required className="mt-1 block w-full border-gray-300 rounded-md shadow-sm" />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Description</label>
            <textarea value={description} onChange={(e) => setDescription(e.target.value)} required className="mt-1 block w-full border-gray-300 rounded-md shadow-sm" rows="4" />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Date limite</label>
            <input type="date" value={deadline} onChange={(e) => setDeadline(e.target.value)} required className="mt-1 block w-full border-gray-300 rounded-md shadow-sm" />
          </div>
          <button type="submit" className="w-full bg-indigo-600 text-white py-2 px-4 rounded hover:bg-indigo-700">Créer l'Offre</button>
          {message && <p className="text-center text-sm text-green-600 mt-4">{message}</p>}
        </form>
      </div>
    </div>
  );
};

export default CreerOffre;
