import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { FaCalendarAlt, FaFileAlt, FaTag, FaCheckCircle } from 'react-icons/fa';
import { motion } from 'framer-motion';

const CreerOffre = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    titre: '',
    description: '',
    domaine: '',
    dateLimite: '',
    typeContrat: 'CDI',
    budget: '',
    lieu: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [success, setSuccess] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    const token = localStorage.getItem('token');

    try {
      await axios.post('http://localhost:3000/api/offres', formData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSuccess(true);
      setTimeout(() => {
        navigate('/ministere');
      }, 1500);
    } catch (error) {
      console.error('Erreur lors de la crÃ©ation de l\'offre', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const contrats = ['CDI', 'CDD', 'Stage', 'Freelance', 'Consultation'];

  return (
    <div className="min-h-screen bg-gray-100 dark:bg-gray-900 p-4 md:p-8 text-gray-900 dark:text-white transition-colors">
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        className="max-w-4xl mx-auto"
      >
        <h2 className="text-2xl md:text-3xl font-bold mb-6 text-center">
          Publier un Nouveau MarchÃ© Public
        </h2>
        
        {success ? (
          <motion.div
            initial={{ scale: 0.8 }}
            animate={{ scale: 1 }}
            className="bg-green-100 dark:bg-green-900 text-green-800 dark:text-green-200 p-6 rounded-lg text-center"
          >
            <FaCheckCircle className="text-5xl mx-auto mb-4" />
            <h3 className="text-xl font-semibold mb-2">Offre publiÃ©e avec succÃ¨s !</h3>
            <p>Redirection en cours...</p>
          </motion.div>
        ) : (
          <motion.form
            onSubmit={handleSubmit}
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className="bg-white dark:bg-gray-800 shadow-lg rounded-xl p-6"
          >
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
              <div>
                <label className="block font-semibold mb-2 flex items-center gap-2">
                  <FaFileAlt /> Titre du marchÃ©
                </label>
                <input
                  type="text"
                  name="titre"
                  value={formData.titre}
                  onChange={handleChange}
                  placeholder="Ex: Fourniture de matÃ©riel informatique"
                  className="w-full px-4 py-2 rounded-lg border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                  required
                />
              </div>

              <div>
                <label className="block font-semibold mb-2 flex items-center gap-2">
                  <FaTag /> Domaine
                </label>
                <input
                  type="text"
                  name="domaine"
                  value={formData.domaine}
                  onChange={handleChange}
                  placeholder="Ex: Informatique, BTP, SantÃ©..."
                  className="w-full px-4 py-2 rounded-lg border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                  required
                />
              </div>

              <div>
                <label className="block font-semibold mb-2 flex items-center gap-2">
                  <FaCalendarAlt /> Date limite
                </label>
                <input
                  type="date"
                  name="dateLimite"
                  value={formData.dateLimite}
                  onChange={handleChange}
                  min={new Date().toISOString().split('T')[0]}
                  className="w-full px-4 py-2 rounded-lg border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                  required
                />
              </div>

              <div>
                <label className="block font-semibold mb-2">Type de contrat</label>
                <select
                  name="typeContrat"
                  value={formData.typeContrat}
                  onChange={handleChange}
                  className="w-full px-4 py-2 rounded-lg border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                >
                  {contrats.map((contrat) => (
                    <option key={contrat} value={contrat}>
                      {contrat}
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block font-semibold mb-2">Budget (optionnel)</label>
                <input
                  type="number"
                  name="budget"
                  value={formData.budget}
                  onChange={handleChange}
                  placeholder="Montant en â¬"
                  className="w-full px-4 py-2 rounded-lg border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                />
              </div>

              <div>
                <label className="block font-semibold mb-2">Lieu (optionnel)</label>
                <input
                  type="text"
                  name="lieu"
                  value={formData.lieu}
                  onChange={handleChange}
                  placeholder="Ex: Paris, Dakar..."
                  className="w-full px-4 py-2 rounded-lg border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                />
              </div>
            </div>

            <div className="mb-6">
              <label className="block font-semibold mb-2">Description dÃ©taillÃ©e</label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleChange}
                rows="6"
                placeholder="DÃ©crivez prÃ©cisÃ©ment le marchÃ©, les spÃ©cifications techniques, les critÃ¨res d'attribution..."
                className="w-full px-4 py-2 rounded-lg border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                required
              ></textarea>
            </div>

            <div className="flex justify-end gap-4">
              <motion.button
                type="button"
                onClick={() => navigate('/ministere')}
                whileHover={{ scale: 1.03 }}
                whileTap={{ scale: 0.97 }}
                className="px-6 py-2 border border-gray-300 dark:border-gray-600 rounded-lg font-medium"
              >
                Annuler
              </motion.button>

              <motion.button
                type="submit"
                disabled={isSubmitting}
                whileHover={{ scale: 1.03 }}
                whileTap={{ scale: 0.97 }}
                className="px-6 py-2 bg-indigo-600 hover:bg-indigo-700 text-white rounded-lg font-medium disabled:opacity-50"
              >
                {isSubmitting ? 'Publication en cours...' : 'Publier le marchÃ©'}
              </motion.button>
            </div>
          </motion.form>
        )}
      </motion.div>
    </div>
  );
};

export default CreerOffre;
