// src/components/Navbar.jsx
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
  const [darkMode, setDarkMode] = useState(() => {
    return localStorage.getItem('theme') === 'dark';
  });

  useEffect(() => {
    const root = window.document.documentElement;
    if (darkMode) {
      root.classList.add('dark');
      localStorage.setItem('theme', 'dark');
    } else {
      root.classList.remove('dark');
      localStorage.setItem('theme', 'light');
    }
  }, [darkMode]);

  const toggleDarkMode = () => setDarkMode(!darkMode);

  return (
    <nav className="bg-indigo-900/70 backdrop-blur-md text-white p-4 px-8 flex justify-between items-center shadow-lg sticky top-0 z-50">
      
      <Link to="/" className="flex items-center space-x-2 group">
        <img
          src="/image/ICON.PNG"
          alt="Logo"
          className="w-8 h-8 object-contain"
        />
        <h1 className="text-lg font-bold tracking-tight group-hover:scale-105 transition-transform duration-200">
          March√©s Publics
        </h1>
      </Link>

      <div className="flex gap-4 items-center">
        <button
          onClick={toggleDarkMode}
          className="text-sm font-semibold bg-white/10 hover:bg-white/20 px-4 py-2 rounded-lg transition-all duration-300 hover:shadow-lg hover:scale-105 border border-white/20 hover:border-white/40"
        >
          {darkMode ? 'Mode Clair' : 'Mode Sombre'}
        </button>

        <Link 
          to="/login" 
          className="text-sm font-semibold bg-white/10 hover:bg-white/20 px-4 py-2 rounded-lg transition-all duration-300 hover:shadow-lg hover:scale-105 border border-white/20 hover:border-white/40"
        >
          Connexion
        </Link>
      </div>
    </nav>
  );
};

export default Navbar;
