import React, { useEffect, useState } from 'react';
import { FaBullseye, FaFileAlt, FaUsers, FaSearch, FaHandshake, FaChartLine, FaGlobe } from 'react-icons/fa';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { motion } from 'framer-motion';

// Animations
const container = {
  hidden: { opacity: 0 },
  show: {
    opacity: 1,
    transition: {
      staggerChildren: 0.1
    }
  }
};

const item = {
  hidden: { opacity: 0, y: 20 },
  show: { opacity: 1, y: 0, transition: { duration: 0.5 } }
};

const fadeIn = {
  hidden: { opacity: 0 },
  show: { opacity: 1, transition: { duration: 0.8 } }
};

const slideUp = {
  hidden: { opacity: 0, y: 30 },
  show: { opacity: 1, y: 0, transition: { duration: 0.6 } }
};

const floatAnimation = {
  y: [0, -15, 0],
  transition: {
    duration: 4,
    repeat: Infinity,
    repeatType: "loop",
    ease: "easeInOut"
  }
};

const Home = () => {
  const navigate = useNavigate();
  const [offers, setOffers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const fetchPublishedOffers = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/offres/publiees');
        setOffers(response.data);
      } catch (error) {
        console.error("Erreur lors du chargement des offres", error);
      } finally {
        setLoading(false);
      }
    };
    fetchPublishedOffers();
  }, []);

  const filteredOffers = offers.filter(offer =>
    offer.title?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    offer.description?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const totalOffers = offers.length;
  const urgentOffers = offers.filter(offer => offer.statut === 'urgent').length;
  const uniqueCategories = [...new Set(offers.map(offer => offer.category || 'Autre'))];

  // Offres en vedette (publicités)
  const featuredOffers = offers.filter(offer => offer.featured).slice(0, 3);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-blue-100 flex flex-col">
      {/* Header */}
      <motion.header 
        initial="hidden"
        animate="show"
        variants={fadeIn}
        className="p-8 text-center mt-6"
      >
        <motion.h1 
          variants={slideUp}
          className="text-4xl font-bold mb-4 text-blue-800"
        >
          Plateforme des Marchés Publics
        </motion.h1>
        <motion.p 
          variants={slideUp}
          className="text-lg max-w-3xl mx-auto text-blue-600"
        >
          Transparence et efficacité dans la gestion des marchés publics
        </motion.p>
      </motion.header>

      {/* Barre de recherche */}
      <motion.div 
        initial="hidden"
        animate="show"
        variants={slideUp}
        transition={{ delay: 0.2 }}
        className="px-6 md:px-20 mt-8 mb-6"
      >
        <div className="relative max-w-2xl mx-auto">
          <FaSearch className="absolute left-4 top-1/2 transform -translate-y-1/2 text-blue-400" />
          <input
            type="text"
            placeholder="Rechercher une offre..."
            className="w-full pl-12 pr-4 py-3 bg-white border border-blue-200 rounded-full text-blue-800 placeholder-blue-400 focus:outline-none focus:ring-2 focus:ring-blue-300 focus:border-transparent"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
      </motion.div>

      {/* Section des offres en vedette */}
      {!loading && featuredOffers.length > 0 && (
        <motion.section
          initial="hidden"
          animate="show"
          variants={slideUp}
          transition={{ delay: 0.3 }}
          className="mb-8 px-6 md:px-20"
        >
          <h2 className="text-2xl font-bold mb-6 text-center text-blue-800">Offres en Vedette</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            {featuredOffers.map((offer, index) => (
              <motion.div
                key={offer.id || index}
                variants={item}
                whileHover={{ scale: 1.03 }}
                className="bg-white rounded-xl shadow-lg overflow-hidden border border-blue-100"
              >
                <div className="p-6">
                  <div className="flex justify-between items-start mb-3">
                    <h3 className="text-lg font-bold text-blue-800">{offer.title}</h3>
                    {offer.statut === 'urgent' && (
                      <span className="bg-red-100 text-red-800 text-xs px-2 py-1 rounded-full">
                        Urgent
                      </span>
                    )}
                  </div>
                  <p className="text-gray-600 text-sm mb-4 line-clamp-3">{offer.description}</p>
                  <div className="flex justify-between items-center text-xs mb-4">
                    <span className="text-blue-600">Date limite: {new Date(offer.deadline).toLocaleDateString()}</span>
                    <span className="text-gray-500">Ref: {offer.reference || "N/A"}</span>
                  </div>
                  <button 
                    onClick={() => navigate(`/offre/${offer.id}`)}
                    className="w-full bg-blue-600 hover:bg-blue-700 text-white py-2 rounded-lg font-medium transition-colors"
                  >
                    Voir l'offre
                  </button>
                </div>
              </motion.div>
            ))}
          </div>
        </motion.section>
      )}

      {/* Statistiques */}
      {!loading && (
        <motion.div 
          initial="hidden"
          animate="show"
          variants={slideUp}
          transition={{ delay: 0.4 }}
          className="bg-white border border-blue-100 rounded-lg shadow-sm p-6 mb-8 text-blue-700 max-w-4xl mx-auto"
        >
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 text-center text-sm font-medium">
            <motion.div variants={item} className="bg-blue-50 p-4 rounded-xl">
              <span className="block text-2xl font-bold text-blue-900">{totalOffers}</span>
              Offres publiées
            </motion.div>
            <motion.div variants={item} className="bg-red-50 p-4 rounded-xl">
              <span className="block text-2xl font-bold text-red-600">{urgentOffers}</span>
              Offres urgentes
            </motion.div>
            <motion.div variants={item} className="bg-green-50 p-4 rounded-xl">
              <span className="block text-2xl font-bold text-green-700">{uniqueCategories.length}</span>
              Domaines différents
            </motion.div>
          </div>
        </motion.div>
      )}

      {/* ... (le reste de votre code reste inchangé) ... */}
    </div>
  );
};

// ... (les composants OfferCard et ServiceCard restent inchangés) ...

export default Home;
