import React, { useEffect, useState } from "react";
import axios from "axios";
import { FaBriefcase, FaCalendarAlt, FaTag, FaSyncAlt } from "react-icons/fa";
import { motion, AnimatePresence } from "framer-motion";

const cardVariants = {
  hidden: { opacity: 0, y: 20 },
  visible: i => ({
    opacity: 1,
    y: 0,
    transition: { delay: i * 0.1 },
  }),
  exit: { opacity: 0, y: -20, transition: { duration: 0.3 } },
};

const OffresEntreprise = () => {
  const [offres, setOffres] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState("");

  const fetchOffres = async () => {
    try {
      setLoading(true);
      const res = await axios.get("/api/offres");
      setOffres(res.data);
    } catch (error) {
      console.error("Erreur chargement offres", error);
      alert("Erreur lors du chargement des offres.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchOffres();
  }, []);

  // Filtrer par domaine si filtre renseigné
  const filteredOffres = filter
    ? offres.filter((offre) =>
        offre.domaine.toLowerCase().includes(filter.toLowerCase())
      )
    : offres;

  return (
    <div className="min-h-screen p-6 md:p-12 bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-white">
      <header className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold">Marchés Publics Disponibles</h1>
        <button
          onClick={fetchOffres}
          title="Rafraîchir"
          className="text-indigo-600 hover:text-indigo-800 dark:hover:text-indigo-400 text-2xl"
        >
          <FaSyncAlt />
        </button>
      </header>

      <div className="mb-6 max-w-sm">
        <input
          type="text"
          placeholder="Filtrer par domaine (ex: informatique, BTP...)"
          className="w-full p-3 rounded-lg border border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-800 focus:ring-2 focus:ring-indigo-500 focus:outline-none"
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
        />
      </div>

      {loading ? (
        <p className="text-center text-lg">Chargement des offres...</p>
      ) : filteredOffres.length === 0 ? (
        <p className="text-center text-lg">Aucune offre trouvée.</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          <AnimatePresence>
            {filteredOffres.map((offre, i) => (
              <motion.div
                key={offre.id}
                custom={i}
                initial="hidden"
                animate="visible"
                exit="exit"
                variants={cardVariants}
                className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6 flex flex-col"
              >
                <h2 className="text-xl font-semibold mb-2 flex items-center gap-2">
                  <FaBriefcase /> {offre.titre}
                </h2>
                <p className="text-gray-700 dark:text-gray-300 mb-3 flex items-center gap-1">
                  <FaTag /> <span>{offre.domaine}</span>
                </p>
                <p className="text-gray-700 dark:text-gray-300 mb-4 flex items-center gap-1">
                  <FaCalendarAlt /> 
                  <span>
                    Date limite:{" "}
                    {new Date(offre.dateLimite).toLocaleDateString("fr-FR")}
                  </span>
                </p>
                <p className="flex-grow whitespace-pre-wrap text-gray-800 dark:text-gray-200">
                  {offre.description.length > 120
                    ? offre.description.slice(0, 120) + "..."
                    : offre.description}
                </p>
              </motion.div>
            ))}
          </AnimatePresence>
        </div>
      )}
    </div>
  );
};

export default OffresEntreprise;
