import React, { createContext, useContext, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const navigate = useNavigate();
  const [user, setUser] = useState(() => {
    const stored = localStorage.getItem('user');
    return stored ? JSON.parse(stored) : null;
  });

  const [isAuthenticated, setIsAuthenticated] = useState(() => {
    return !!localStorage.getItem('token');
  });

  // Vérifier l'authentification au chargement
  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUser = localStorage.getItem('user');
    
    if (token && storedUser) {
      setUser(JSON.parse(storedUser));
      setIsAuthenticated(true);
    } else {
      setIsAuthenticated(false);
    }
  }, []);

  const login = ({ token, role, ...userData }) => {
    console.log('🔐 AuthContext: Début de la connexion avec role:', role);
    
    try {
      // Sauvegarder les données
      localStorage.setItem('token', token);
      localStorage.setItem('user', JSON.stringify({ role, ...userData }));
      
      // Mettre à jour l'état
      setUser({ token, role, ...userData });
      setIsAuthenticated(true);

      console.log('✅ AuthContext: Données sauvegardées, redirection...');

      // Redirection selon rôle avec un petit délai pour s'assurer que l'état est mis à jour
      setTimeout(() => {
        if (['ministere', 'admin', 'ministerepublique'].includes(role)) {
          console.log('➡️ Redirection vers /ministere/home');
          navigate('/ministere/home', { replace: true });
        } else if (role === 'entreprise') {
          console.log('➡️ Redirection vers /entreprise/home');
          navigate('/entreprise/home', { replace: true });
        } else {
          console.log('➡️ Redirection vers /');
          navigate('/', { replace: true });
        }
      }, 100);

    } catch (error) {
      console.error('❌ Erreur dans AuthContext login:', error);
    }
  };

  const logout = () => {
    console.log('🚪 Déconnexion en cours...');
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    setIsAuthenticated(false);
    navigate('/login', { replace: true });
  };

  return (
    <AuthContext.Provider value={{ 
      user, 
      isAuthenticated, 
      login, 
      logout 
    }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};
