import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { FaSearch } from 'react-icons/fa';

const slideUp = {
  hidden: { opacity: 0, y: 40 },
  show: { opacity: 1, y: 0 }
};

const item = {
  hidden: { opacity: 0, y: 20 },
  show: { opacity: 1, y: 0 }
};

const images = [
  {
    src: '/TP.jpeg',
    text: "Trouvez les meilleures opportunités publiques dès aujourd'hui."
  },
  {
    src: '/TPR.jpeg',
    text: "Répondez rapidement aux offres urgentes de l'État."
  },
  {
    src: '/AA.jpeg',
    text: "Explorez les marchés par secteur et spécialité."
  }
];

const PageAccueil = ({ searchTerm, setSearchTerm, totalOffers, urgentOffers, uniqueCategories, loading }) => {
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrent((prev) => (prev + 1) % images.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="w-full">
      {/* Carrousel publicitaire */}
      <div className="relative w-full h-[300px] md:h-[400px] overflow-hidden rounded-xl shadow-md mb-10">
        {images.map((img, index) => (
          <motion.div
            key={index}
            className={`absolute top-0 left-0 w-full h-full transition-opacity duration-1000 ease-in-out ${index === current ? 'opacity-100 z-10' : 'opacity-0 z-0'}`}
            initial={{ opacity: 0 }}
            animate={{ opacity: index === current ? 1 : 0 }}
            transition={{ duration: 1 }}
          >
            <img
              src={img.src}
              alt={`Slide ${index}`}
              className="w-full h-full object-cover"
            />
            <div className="absolute inset-0 bg-black bg-opacity-50 flex items-center justify-center">
              <h2 className="text-white text-xl md:text-3xl font-bold text-center px-4">
                {img.text}
              </h2>
            </div>
          </motion.div>
        ))}
      </div>

      {/* Barre de recherche */}
      <motion.div 
        initial="hidden"
        animate="show"
        variants={slideUp}
        transition={{ delay: 0.2 }}
        className="px-6 md:px-20 mt-4 mb-6"
      >
        <div className="relative max-w-2xl mx-auto">
          <FaSearch className="absolute left-4 top-1/2 transform -translate-y-1/2 text-blue-400" />
          <input
            type="text"
            placeholder="Rechercher une offre..."
            className="w-full pl-12 pr-4 py-3 bg-white border border-blue-200 rounded-full text-blue-800 placeholder-blue-400 focus:outline-none focus:ring-2 focus:ring-blue-300 focus:border-transparent"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
      </motion.div>

      {/* Résumé des offres */}
      {!loading && (
        <motion.div 
          initial="hidden"
          animate="show"
          variants={slideUp}
          transition={{ delay: 0.3 }}
          className="bg-white border border-blue-100 rounded-lg shadow-sm p-4 mb-8 text-blue-700 max-w-4xl mx-auto"
        >
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-center text-sm font-medium">
            <motion.div variants={item}>
              <span className="block text-xl font-bold text-blue-900">{totalOffers}</span>
              Offres publiées
            </motion.div>
            <motion.div variants={item}>
              <span className="block text-xl font-bold text-red-600">{urgentOffers}</span>
              Offres urgentes
            </motion.div>
            <motion.div variants={item}>
              <span className="block text-xl font-bold text-green-700">{uniqueCategories.length}</span>
              Domaines différents
            </motion.div>
          </div>
        </motion.div>
      )}
    </div>
  );
};

export default PageAccueil;
