// src/Pages/Entreprise/MesOffres.jsx
import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import axios from 'axios';
import { motion } from 'framer-motion';

const MesOffres = () => {
  const { user } = useAuth();
  const [offres, setOffres] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchOffres = async () => {
      try {
        const response = await axios.get(
          `http://localhost:3000/api/marches/entreprise/${user.entrepriseId}`,
          {
            headers: { Authorization: `Bearer ${user.token}` }
          }
        );
        setOffres(response.data);
      } catch (error) {
        console.error("Erreur récupération marchés:", error);
      } finally {
        setLoading(false);
      }
    };

    if (user?.entrepriseId) fetchOffres();
  }, [user]);

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">Marchés attribués</h1>
      
      {loading ? (
        <div className="flex justify-center">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
        </div>
      ) : offres.length === 0 ? (
        <div className="text-center py-12 bg-gray-100 rounded-lg">
          <p className="text-lg">Aucun marché attribué actuellement</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {offres.map((offre) => (
            <motion.div
              key={offre._id}
              whileHover={{ y: -5 }}
              className="border rounded-lg overflow-hidden shadow-md"
            >
              <div className="p-6 bg-white">
                <div className="flex justify-between items-start mb-3">
                  <h3 className="text-xl font-semibold">{offre.titre}</h3>
                  <span className="bg-blue-100 text-blue-800 text-xs px-2 py-1 rounded-full">
                    {offre.typeMarche}
                  </span>
                </div>
                
                <div className="space-y-2 mb-4 text-sm text-gray-600">
                  <p><strong>Référence:</strong> {offre.reference}</p>
                  <p><strong>Date limite:</strong> {new Date(offre.dateLimite).toLocaleDateString()}</p>
                  {offre.budget && <p><strong>Budget:</strong> {offre.budget.toLocaleString()} €</p>}
                </div>
                
                <button className="mt-4 w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition">
                  Voir détails
                </button>
              </div>
            </motion.div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MesOffres;
