import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { FaSearch, FaUser, FaEnvelope, FaPhone, FaFileAlt, FaCheck, FaTimes, FaClock } from 'react-icons/fa';

const GestionCandidatures = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedStatus, setSelectedStatus] = useState('tous');

  // Données de démonstration
  const [candidatures, setCandidatures] = useState([
    {
      id: 1,
      nom: 'Entreprise A',
      email: 'contact@companya.com',
      telephone: '0123456789',
      offre: 'Développeur Fullstack',
      cv: 'cv_entreprise_a.pdf',
      status: 'en_attente',
      date: '2023-05-15'
    },
    {
      id: 2,
      nom: 'Entreprise B',
      email: 'rh@companyb.com',
      telephone: '0987654321',
      offre: 'Chef de projet',
      cv: 'cv_entreprise_b.pdf',
      status: 'accepte',
      date: '2023-05-10'
    },
    {
      id: 3,
      nom: 'Entreprise C',
      email: 'info@companyc.com',
      telephone: '0678912345',
      offre: 'Designer UI/UX',
      cv: 'cv_entreprise_c.pdf',
      status: 'rejete',
      date: '2023-05-05'
    }
  ]);

  const filteredCandidatures = candidatures.filter(candidature => {
    const matchesSearch = candidature.nom.toLowerCase().includes(searchTerm.toLowerCase()) || 
                         candidature.offre.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = selectedStatus === 'tous' || candidature.status === selectedStatus;
    return matchesSearch && matchesStatus;
  });

  const updateStatus = (id, newStatus) => {
    setCandidatures(candidatures.map(c => 
      c.id === id ? { ...c, status: newStatus } : c
    ));
  };

  const getStatusColor = (status) => {
    switch(status) {
      case 'accepte': return 'bg-green-100 text-green-800';
      case 'rejete': return 'bg-red-100 text-red-800';
      default: return 'bg-yellow-100 text-yellow-800';
    }
  };

  const getStatusIcon = (status) => {
    switch(status) {
      case 'accepte': return <FaCheck className="mr-1" />;
      case 'rejete': return <FaTimes className="mr-1" />;
      default: return <FaClock className="mr-1" />;
    }
  };

  return (
    <div className={`min-h-screen transition-colors duration-300 ${darkMode ? 'bg-gray-900 text-gray-100' : 'bg-gray-50 text-gray-900'}`}>
      <div className="container mx-auto px-4 py-8">
        <div className="flex justify-between items-center mb-8">
          <motion.h1 
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5 }}
            className="text-3xl font-bold"
          >
            Gestion des candidatures
          </motion.h1>
          <button
            onClick={() => setDarkMode(!darkMode)}
            className={`px-4 py-2 rounded-lg ${darkMode ? 'bg-yellow-400 text-gray-900' : 'bg-gray-800 text-white'}`}
          >
            {darkMode ? '☀️ Light Mode' : '🌙 Dark Mode'}
          </button>
        </div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2, duration: 0.5 }}
          className={`p-6 rounded-xl shadow-lg mb-8 ${darkMode ? 'bg-gray-800' : 'bg-white'}`}
        >
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="relative">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <FaSearch className="text-gray-400" />
              </div>
              <input
                type="text"
                placeholder="Rechercher..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className={`pl-10 w-full p-3 rounded-lg border ${darkMode ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
              />
            </div>

            <select
              value={selectedStatus}
              onChange={(e) => setSelectedStatus(e.target.value)}
              className={`p-3 rounded-lg border ${darkMode ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
            >
              <option value="tous">Tous les statuts</option>
              <option value="en_attente">En attente</option>
              <option value="accepte">Accepté</option>
              <option value="rejete">Rejeté</option>
            </select>

            <div className="flex items-center justify-end">
              <span className={`px-3 py-1 rounded-full ${darkMode ? 'bg-blue-900 text-blue-100' : 'bg-blue-100 text-blue-800'}`}>
                {filteredCandidatures.length} candidature(s)
              </span>
            </div>
          </div>
        </motion.div>

        <AnimatePresence>
          {filteredCandidatures.length > 0 ? (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 0.5 }}
              className="space-y-4"
            >
              {filteredCandidatures.map((candidature) => (
                <motion.div
                  key={candidature.id}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -20 }}
                  transition={{ duration: 0.3 }}
                  className={`p-6 rounded-xl shadow-lg ${darkMode ? 'bg-gray-800' : 'bg-white'}`}
                >
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                    <div className="space-y-2">
                      <div className="flex items-center">
                        <FaUser className="mr-2 text-blue-500" />
                        <h3 className="text-xl font-semibold">{candidature.nom}</h3>
                      </div>
                      <div className="flex items-center">
                        <FaEnvelope className="mr-2 text-blue-500" />
                        <span>{candidature.email}</span>
                      </div>
                      <div className="flex items-center">
                        <FaPhone className="mr-2 text-blue-500" />
                        <span>{candidature.telephone}</span>
                      </div>
                    </div>

                    <div className="space-y-2">
                      <h4 className="font-medium">Offre postulée:</h4>
                      <p className="text-lg">{candidature.offre}</p>
                      <div className="flex items-center">
                        <FaFileAlt className="mr-2 text-blue-500" />
                        <a 
                          href={`/documents/${candidature.cv}`} 
                          target="_blank" 
                          rel="noopener noreferrer"
                          className="text-blue-500 hover:underline"
                        >
                          Voir le dossier
                        </a>
                      </div>
                    </div>

                    <div className="flex flex-col justify-between">
                      <div className="flex justify-between items-center mb-4">
                        <span className={`px-3 py-1 rounded-full flex items-center ${getStatusColor(candidature.status)}`}>
                          {getStatusIcon(candidature.status)}
                          {candidature.status === 'en_attente' ? 'En attente' : 
                           candidature.status === 'accepte' ? 'Accepté' : 'Rejeté'}
                        </span>
                        <span className={`text-sm ${darkMode ? 'text-gray-400' : 'text-gray-500'}`}>
                          {candidature.date}
                        </span>
                      </div>

                      <div className="flex space-x-2">
                        <motion.button
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                          onClick={() => updateStatus(candidature.id, 'accepte')}
                          className="px-4 py-2 bg-green-500 text-white rounded-lg flex-1"
                        >
                          Accepter
                        </motion.button>
                        <motion.button
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                          onClick={() => updateStatus(candidature.id, 'rejete')}
                          className="px-4 py-2 bg-red-500 text-white rounded-lg flex-1"
                        >
                          Rejeter
                        </motion.button>
                      </div>
                    </div>
                  </div>
                </motion.div>
              ))}
            </motion.div>
          ) : (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 0.5 }}
              className={`p-8 text-center rounded-xl ${darkMode ? 'bg-gray-800' : 'bg-white'} shadow-lg`}
            >
              <p className="text-xl">Aucune candidature trouvée</p>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default GestionCandidatures;
