import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaSearch } from 'react-icons/fa';
import { motion } from 'framer-motion';

const container = {
  hidden: { opacity: 0, y: 50 },
  show: {
    opacity: 1,
    y: 0,
    transition: {
      staggerChildren: 0.2
    }
  }
};

const item = {
  hidden: { opacity: 0, y: 20 },
  show: { opacity: 1, y: 0 }
};

const ListeOffres = () => {
  const [offres, setOffres] = useState([]);
  const [filteredOffres, setFilteredOffres] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const [urgentOffers, setUrgentOffers] = useState(0);
  const [totalOffers, setTotalOffers] = useState(0);
  const [uniqueCategories, setUniqueCategories] = useState([]);

  useEffect(() => {
    const fetchOffres = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/offres');
        const data = response.data;
        const offresValidees = data.filter(offre => offre.status === 'validée');

        setOffres(offresValidees);
        setFilteredOffres(offresValidees);
        setTotalOffers(offresValidees.length);
        setUrgentOffers(offresValidees.filter(offre => offre.urgence === 'oui').length);

        const categories = [...new Set(offresValidees.map(offre => offre.categorie))];
        setUniqueCategories(categories);

        setLoading(false);
      } catch (error) {
        console.error('Erreur lors de la récupération des offres', error);
        setLoading(false);
      }
    };

    fetchOffres();
  }, []);

  const handleSearch = (e) => {
    const term = e.target.value;
    setSearchTerm(term);
    const filtered = offres.filter(offre =>
      offre.titre.toLowerCase().includes(term.toLowerCase()) ||
      offre.categorie.toLowerCase().includes(term.toLowerCase())
    );
    setFilteredOffres(filtered);
  };

  return (
    <div className="container mx-auto px-4 py-8">

      <h1 className="text-3xl font-bold mb-4 text-center text-blue-900">
        Liste des Appels d’Offres
      </h1>

      {/* Résumé des offres */}
      {!loading && (
        <motion.div
          initial="hidden"
          whileInView="show"
          viewport={{ once: false, amount: 0.3 }}
          variants={container}
          className="bg-white border border-blue-100 rounded-lg shadow-sm p-4 mb-8 text-blue-700 max-w-4xl mx-auto"
        >
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-center text-sm font-medium">
            <motion.div variants={item}>
              <span className="block text-xl font-bold text-blue-900">{totalOffers}</span>
              Offres publiées
            </motion.div>
            <motion.div variants={item}>
              <span className="block text-xl font-bold text-red-600">{urgentOffers}</span>
              Offres urgentes
            </motion.div>
            <motion.div variants={item}>
              <span className="block text-xl font-bold text-green-700">{uniqueCategories.length}</span>
              Domaines différents
            </motion.div>
          </div>
        </motion.div>
      )}

      {/* Barre de recherche */}
      <div className="flex justify-center mb-6">
        <div className="relative w-full md:w-1/2">
          <input
            type="text"
            placeholder="Rechercher un appel d'offre..."
            className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            value={searchTerm}
            onChange={handleSearch}
          />
          <FaSearch className="absolute left-3 top-3 text-gray-400" />
        </div>
      </div>

      {/* Liste des offres */}
      <div className="grid gap-6">
        {loading ? (
          <p className="text-center text-gray-600">Chargement en cours...</p>
        ) : filteredOffres.length === 0 ? (
          <p className="text-center text-gray-600">Aucune offre trouvée.</p>
        ) : (
          filteredOffres.map(offre => (
            <div
              key={offre.id}
              className="bg-white p-6 rounded-lg shadow-md border-l-4 border-blue-500 hover:shadow-lg transition duration-300"
            >
              <h2 className="text-xl font-semibold text-blue-800 mb-2">{offre.titre}</h2>
              <p className="text-gray-700 mb-1"><span className="font-medium">Catégorie :</span> {offre.categorie}</p>
              <p className="text-gray-700 mb-1"><span className="font-medium">Date limite :</span> {offre.dateLimite}</p>
              <p className="text-gray-700 mb-2"><span className="font-medium">Description :</span> {offre.description}</p>
              <span className={`inline-block px-2 py-1 text-xs font-bold rounded-full ${offre.urgence === 'oui' ? 'bg-red-100 text-red-700' : 'bg-green-100 text-green-700'}`}>
                {offre.urgence === 'oui' ? 'Urgent' : 'Normal'}
              </span>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default ListeOffres;
