import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaCheck, FaClipboardList, FaPaperPlane } from 'react-icons/fa';

const DashboardEntreprise = () => {
  const [offers, setOffers] = useState([]);
  const [candidatures, setCandidatures] = useState([]);
  const [message, setMessage] = useState('');
  const [formState, setFormState] = useState({ offerId: '', message: '' });

  const token = localStorage.getItem('token');

  useEffect(() => {
    fetchDashboard();
    fetchCandidatures();
  }, []);

  const fetchDashboard = async () => {
    try {
      const res = await axios.get('http://localhost:3000/api/dashboard/entreprise', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOffers(res.data.offers || []);
    } catch (err) {
      console.error('Erreur chargement offres:', err);
    }
  };

  const fetchCandidatures = async () => {
    try {
      const res = await axios.get('http://localhost:3000/api/mes-candidatures', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setCandidatures(res.data || []);
    } catch (err) {
      console.error('Erreur chargement candidatures:', err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formState.offerId || !formState.message) return;

    try {
      await axios.post('http://localhost:3000/api/candidatures', formState, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessage('Candidature envoyée avec succès.');
      setFormState({ offerId: '', message: '' });
      fetchCandidatures();
    } catch (err) {
      console.error('Erreur lors de la candidature:', err);
    }
  };

  return (
    <div className="min-h-screen p-10 bg-gradient-to-br from-purple-900 to-indigo-900 text-white">
      <h1 className="text-3xl font-bold mb-8 text-center">Espace Entreprise</h1>

      {/* Liste des offres */}
      <section className="mb-12">
        <h2 className="text-2xl font-semibold mb-4 flex items-center gap-2"><FaClipboardList /> Offres disponibles</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {offers.map((offer) => (
            <div key={offer.id} className="bg-white bg-opacity-10 p-4 rounded-xl shadow">
              <h3 className="text-lg font-bold">{offer.title}</h3>
              <p className="text-sm text-gray-300 mb-2">{offer.description}</p>
              <p className="text-xs text-indigo-200">Date limite : {offer.deadline}</p>
              <button
                className="mt-3 bg-green-600 hover:bg-green-700 px-4 py-1 rounded text-sm"
                onClick={() => setFormState({ ...formState, offerId: offer.id })}
              >
                Postuler
              </button>
            </div>
          ))}
        </div>
      </section>

      {/* Formulaire de candidature */}
      <section className="mb-12">
        <h2 className="text-2xl font-semibold mb-4 flex items-center gap-2"><FaPaperPlane /> Envoyer une candidature</h2>
        <form onSubmit={handleSubmit} className="bg-white bg-opacity-10 p-6 rounded-xl max-w-xl mx-auto shadow space-y-4">
          <div>
            <label className="block text-sm mb-1">ID de l'offre</label>
            <input
              type="text"
              className="w-full px-4 py-2 rounded text-black"
              value={formState.offerId}
              onChange={(e) => setFormState({ ...formState, offerId: e.target.value })}
              placeholder="ex: 1727168291"
            />
          </div>
          <div>
            <label className="block text-sm mb-1">Message de motivation</label>
            <textarea
              className="w-full px-4 py-2 rounded text-black"
              value={formState.message}
              onChange={(e) => setFormState({ ...formState, message: e.target.value })}
              placeholder="Expliquez pourquoi vous postulez..."
            />
          </div>
          <button type="submit" className="bg-blue-600 hover:bg-blue-700 px-6 py-2 rounded text-sm">
            Envoyer
          </button>
          {message && <p className="text-green-300 mt-2">{message}</p>}
        </form>
      </section>

      {/* Liste des candidatures */}
      <section>
        <h2 className="text-2xl font-semibold mb-4 flex items-center gap-2"><FaCheck /> Mes candidatures envoyées</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {candidatures.map((c) => (
            <div key={c.id} className="bg-white bg-opacity-10 p-4 rounded-xl shadow">
              <p className="text-sm text-indigo-200 mb-1">Offre ID : {c.offerId}</p>
              <p className="text-gray-300 text-sm">{c.message}</p>
              <p className="text-xs text-gray-400 italic mt-2">Envoyé le : {new Date(c.date).toLocaleDateString()}</p>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default DashboardEntreprise;
