// src/pages/Ministere/CreerOffre.jsx
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const CreerOffre = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    titre: '',
    description: '',
    domaine: '',
    dateLimite: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      await axios.post('http://localhost:3000/api/offres', formData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      navigate('/ministere'); // Redirige vers la page d'accueil du ministère
    } catch (error) {
      console.error('Erreur lors de la création de l\'offre', error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 dark:bg-gray-900 p-8 text-gray-900 dark:text-white transition">
      <h2 className="text-3xl font-bold mb-6 text-center">Créer une Nouvelle Offre</h2>
      <form onSubmit={handleSubmit} className="max-w-xl mx-auto bg-white dark:bg-gray-800 shadow-lg rounded-xl p-6">
        <div className="mb-4">
          <label className="block font-semibold mb-1">Titre de l'offre</label>
          <input
            type="text"
            name="titre"
            value={formData.titre}
            onChange={handleChange}
            className="w-full px-4 py-2 rounded-lg bg-gray-100 dark:bg-gray-700 text-gray-900 dark:text-white"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block font-semibold mb-1">Description</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            rows="4"
            className="w-full px-4 py-2 rounded-lg bg-gray-100 dark:bg-gray-700 text-gray-900 dark:text-white"
            required
          ></textarea>
        </div>
        <div className="mb-4">
          <label className="block font-semibold mb-1">Domaine</label>
          <input
            type="text"
            name="domaine"
            value={formData.domaine}
            onChange={handleChange}
            className="w-full px-4 py-2 rounded-lg bg-gray-100 dark:bg-gray-700 text-gray-900 dark:text-white"
            required
          />
        </div>
        <div className="mb-6">
          <label className="block font-semibold mb-1">Date limite</label>
          <input
            type="date"
            name="dateLimite"
            value={formData.dateLimite}
            onChange={handleChange}
            className="w-full px-4 py-2 rounded-lg bg-gray-100 dark:bg-gray-700 text-gray-900 dark:text-white"
            required
          />
        </div>
        <button
          type="submit"
          className="w-full bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-2 rounded-lg transition"
        >
          Publier l'offre
        </button>
      </form>
    </div>
  );
};

export default CreerOffre;
