import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { FaClipboardList, FaPaperPlane, FaChartLine } from 'react-icons/fa';

const DashboardEntreprise = () => {
  const [stats, setStats] = useState({
    totalCandidatures: 0,
    enAttente: 0,
    acceptees: 0
  });

  useEffect(() => {
    // Simuler des données
    setStats({
      totalCandidatures: 12,
      enAttente: 5,
      acceptees: 3
    });
  }, []);

  return (
    <div className="min-h-screen p-6 bg-gradient-to-br from-purple-900 to-indigo-900 text-white">
      <h1 className="text-3xl font-bold mb-8 text-center">Espace Entreprise</h1>
      
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-12">
        <StatCard 
          icon={<FaClipboardList size={24} />}
          title="Offres disponibles"
          value="25"
          link="/entreprise/offres"
          linkText="Voir les offres"
        />
        <StatCard 
          icon={<FaPaperPlane size={24} />}
          title="Candidatures envoyées"
          value={stats.totalCandidatures}
          link="/entreprise/mes-candidatures"
          linkText="Voir le détail"
        />
        <StatCard 
          icon={<FaChartLine size={24} />}
          title="Statut des candidatures"
          value={`${stats.acceptees} acceptées`}
          link="/entreprise/mes-candidatures"
          linkText="Voir le suivi"
        />
      </div>

      <div className="text-center">
        <h2 className="text-xl font-semibold mb-4">Actions rapides</h2>
        <div className="flex justify-center gap-4">
          <Link 
            to="/entreprise/postuler" 
            className="bg-blue-600 hover:bg-blue-700 px-6 py-3 rounded-lg"
          >
            Postuler à une offre
          </Link>
          <Link 
            to="/entreprise/mes-candidatures" 
            className="bg-purple-600 hover:bg-purple-700 px-6 py-3 rounded-lg"
          >
            Voir mes candidatures
          </Link>
        </div>
      </div>
    </div>
  );
};

const StatCard = ({ icon, title, value, link, linkText }) => (
  <div className="bg-white bg-opacity-10 p-6 rounded-xl shadow-lg">
    <div className="flex items-center gap-3 mb-4">
      <div className="text-indigo-300">{icon}</div>
      <h3 className="text-lg font-semibold">{title}</h3>
    </div>
    <p className="text-2xl font-bold mb-4">{value}</p>
    <Link to={link} className="text-indigo-300 hover:text-indigo-200 text-sm">
      {linkText} →
    </Link>
  </div>
);

export default DashboardEntreprise;
