import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';

const DashboardMinistere = ({ token }) => {
  const [offres, setOffres] = useState([]);

  const fetchOffers = () => {
    fetch('http://localhost:3000/api/dashboard/ministere', {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then(res => res.json())
      .then(data => setOffres(data.recentOffers || []));
  };

  const handleAction = (id, action) => {
    fetch(`http://localhost:3000/api/ministere/${action}/${id}`, {
      method: 'POST',
      headers: { Authorization: `Bearer ${token}` },
    }).then(fetchOffers);
  };

  useEffect(() => {
    fetchOffers();
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Mes Offres</h1>
      <div className="space-y-4">
        {offres.map(offre => (
          <motion.div
            key={offre.id}
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            className="bg-white p-4 rounded shadow border"
          >
            <h2 className="font-semibold text-indigo-700">{offre.title}</h2>
            <p className="text-sm text-gray-600">{offre.description}</p>
            <div className="mt-2 flex gap-2">
              <button onClick={() => handleAction(offre.id, 'valider')} className="bg-green-600 px-3 py-1 text-white rounded">Valider</button>
              <button onClick={() => handleAction(offre.id, 'rejeter')} className="bg-red-600 px-3 py-1 text-white rounded">Rejeter</button>
            </div>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

export default DashboardMinistere;
